        for (WebSocket webSocket : webSocketSet.values()) {
            try {
                webSocket.session.getBasicRemote().sendText(message);
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void AppointSending(String targetName, String message) {
