import java.util.concurrent.ThreadFactory;

public class HelloWorldServer {
    private Server server;
    private static final Logger logger = LoggerFactory.getLogger(HelloWorldServer.class);
    private static final int PORT = 50051;

    public void start() throws IOException {
        this.server = ServerBuilder.forPort(PORT)
                .addService(new GreeterImpl())
                .build()
                .start();
        logger.info("Server started on port {}", PORT);
        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            logger.info("Shutting down gRPC server");
            try {
                stop();
            } catch (InterruptedException e) {
                logger.error("Error stopping server", e);
            }
        }));
    }

    public void stop() throws InterruptedException {
        if (server != null) {
            server.shutdown().awaitTermination(5, TimeUnit.SECONDS);
        }
    }

    public void blockUntilShutdown() throws InterruptedException {
        if (server != null) {
            server.awaitTermination();
        }
    }

    class GreeterImpl extends StreamingGreeterGrpc.StreamingGreeterImplBase {
        @Override
        public void sayHelloStreaming(HelloRequest req, StreamObserver<HelloReply> responseObserver) {
            String name = req.getName();
            for (int i = 1; i <= 3; i++) {
                String message = "Hello " + name + "!".repeat(i);
                HelloReply reply = HelloReply.newBuilder().setMessage(message).build();
                responseObserver.onNext(reply);
            }
            responseObserver.onCompleted();
        }
    }

    public static void main(String[] args) throws IOException, InterruptedException {
        HelloWorldServer server = new HelloWorldServer();
        server.start();
        server.blockUntilShutdown();
