import io.grpc.Server;
import io.grpc.ServerBuilder;
import io.grpc.ServerCall;
import io.grpc.ServerCallHandler;
import io.grpc.ServerInterceptor;
import io.grpc.Metadata;
import brave.propagation.Extractor;
import brave.propagation.Propagation;
import java.util.Map;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.LinkedBlockingQueue;
import java.util.concurrent.TimeUnit;

public class TestServer {
    private final BlockingQueue<Long> delayQueue = new LinkedBlockingQueue<>();
    private final BlockingQueue<Metadata> headers = new LinkedBlockingQueue<>();
    private final BlockingQueue<TraceContextOrSamplingFlags> requests = new LinkedBlockingQueue<>();
    private final Extractor<GrpcServerRequest> extractor;
    private final Server server;
    private final ServerInterceptor tracingInterceptor = new ServerInterceptor() {
        @Override
        public <ReqT, RespT> ServerCall.Listener<ReqT> interceptCall(ServerCall<ReqT, RespT> call, Metadata headers, ServerCallHandler<ReqT, RespT> next) {
            GrpcServerRequest carrier = new GrpcServerRequest(headers);
            TraceContextOrSamplingFlags context = extractor.extract(carrier);
            try {
                TestServer.this.headers.put(headers);
                TestServer.this.requests.put(context);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
            Long delay = delayQueue.poll();
            if (delay != null) {
                try {
                    Thread.sleep(delay);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            }
            return next.startCall(new DelegatingServerCall<>(call), headers);
        }
    };

    public TestServer(Map<String, Key<String>> nameToKey, Propagation<String> propagation) {
        this.extractor = propagation.toExtractor((carrier, key) -> {
            Key<String> k = nameToKey.get(key);
            return carrier.getHeaders().get(k.name());
        });
        this.server = ServerBuilder.forPort(0)
            .addService(ServerServiceDefinition.create())
            .intercept(tracingInterceptor)
            .build();
    }

    public void start() throws IOException {
        server.start();
    }

    public void stop() {
        server.shutdown();
        try {
            server.awaitTermination(5, TimeUnit.SECONDS);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    public int port() {
        return server.getPort();
    }

    public TraceContextOrSamplingFlags takeRequest() {
        try {
            return requests.poll(3, TimeUnit.SECONDS);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return null;
        }
    }

    public void enqueueDelay(long millis) {
        try {
            delayQueue.put(millis);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    private static class GrpcServerRequest {
        private final Metadata headers;
        GrpcServerRequest(Metadata headers) {
            this.headers = headers;
        }
        Metadata getHeaders() {
            return headers;
        }
    }

    private static class DelegatingServerCall<ReqT, RespT> extends ServerCall<ReqT, RespT> {
        private final ServerCall<ReqT, RespT> delegate;
        DelegatingServerCall(ServerCall<ReqT, RespT> delegate) {
            this.delegate = delegate;
        }
        @Override
        public void start(Listener<ReqT> listener) {
            delegate.start(listener);
        }
        @Override
        public void request(int numMessages) {
            delegate.request(numMessages);
        }
        @Override
        public void sendHeaders(Metadata headers) {
            delegate.sendHeaders(headers);
        }
        @Override
        public void close(Status status, Metadata trailers) {
            delegate.close(status, trailers);
        }
        @Override
        public void halfClose() {
            delegate.halfClose();
        }
        @Override
        public void setMessageCompression(boolean enable) {
            delegate.setMessageCompression(enable);
        }
        @Override
        public boolean isReady() {
            return delegate.isReady();
        }
        @Override
        public void setOnReadyHandler(Runnable onReadyHandler) {
            delegate.setOnReadyHandler(onReadyHandler);
        }
        @Override
        public void cancel() {
            delegate.cancel();
        }
        @Override
        public boolean isCancelled() {
            return delegate.isCancelled();
        }
        @Override
        public Metadata getAttributes() {
            return delegate.getAttributes();
        }
        @Override
        public String getMethod() {
            return delegate.getMethod();
        }
        @Override
        public String getAuthority() {
            return delegate.getAuthority();
        }
        @Override
        public String getHost() {
            return delegate.getHost();
        }
        @Override
        public int getPort() {
            return delegate.getPort();
        }
        @Override
        public void setCompression(String compression) {
            delegate.setCompression(compression);
        }
        @Override
        public String getCompression() {
            return delegate.getCompression();
        }
    }
}

