import io.grpc.Server;
import io.grpc.ServerBuilder;
import io.grpc.BindableService;
import java.util.Map;
import lombok.extern.slf4j.Slf4j;

@Component
@Slf4j
public class GrpcLauncher {

    @Value("${grpc.server.port}")
    private int grpcServerPort;

    private Server server;

    private static final int DEFAULT_MAX_MESSAGE_SIZE = 10 * 1024 * 1024;

    public void grpcStart(Map<String, Object> grpcServiceBeanMap) {
        ServerBuilder<?> serverBuilder = ServerBuilder.forPort(grpcServerPort);
        for (Object serviceBean : grpcServiceBeanMap.values()) {
            if (serviceBean instanceof BindableService) {
                serverBuilder.addService((BindableService) serviceBean);
            } else {
                log.warn("Service bean is not a BindableService: {}", serviceBean.getClass());
            }
        }
        serverBuilder.maxInboundMessageSize(DEFAULT_MAX_MESSAGE_SIZE);
        try {
            this.server = serverBuilder.build().start();
            log.info("gRPC server started on port {}", grpcServerPort);
            Runtime.getRuntime().addShutdownHook(new Thread(() -> {
                log.info("Shutdown hook executed.");
                grpcStop();
            }));
        } catch (Exception e) {
            log.error("Failed to start gRPC server", e);
            throw new RuntimeException(e);
        }
    }

    public void grpcStop() {
        if (server != null) {
            try {
                server.shutdown().awaitTermination(5, java.util.concurrent.TimeUnit.SECONDS);
                log.info("gRPC server stopped successfully.");
            } catch (InterruptedException e) {
                log.error("gRPC server shutdown interrupted", e);
                Thread.currentThread().interrupt();
            }
        }
    }

    @PreDestroy
    public void destroy() {
        grpcStop();
    }
}
