    private static final int PROMETHEUS_PORT = 8080;
    private final Counter syncRequests = Counter.build("sync_requests_total", "Total synchronous requests").register();
    private final Counter streamingRequests = Counter.build("streaming_requests_total", "Total streaming requests").register();
    private Server grpcServer;
    private HTTPServer prometheusHttpServer;

    public void start() throws IOException {
