                .build();
        grpcServer.start();
        LOG.info("Server started on port {}", PORT);

        prometheusHttpServer = new HTTPServer(PROMETHEUS_PORT);
        LOG.info("Prometheus metrics exposed on port {}", PROMETHEUS_PORT);
    }

    public void blockUntilShutdown() throws InterruptedException {
        if (grpcServer != null) {
            grpcServer.awaitTermination();
        }
    }

    public void stop() throws InterruptedException {
        if (grpcServer != null) {
            grpcServer.shutdownNow().awaitTermination();
        }
        if (prometheusHttpServer != null) {
            prometheusHttpServer.stop();
        }
    }

    class DataImpl extends DataServiceGrpc.DataServiceImplBase {
