public class MockImapServer {
    private Deque<ImapInteraction> interactions;
    private CountDownLatch waitForConnectionClosed;
    private CountDownLatch waitForAllExpectedCommands;
    private MockServerThread mockServerThread;
    private String host;
    private int port;

    public void start() throws IOException {
        ServerSocket serverSocket = new ServerSocket(0);
        this.host = "localhost";
        this.port = serverSocket.getLocalPort();
        this.mockServerThread = new MockServerThread(serverSocket, interactions, waitForConnectionClosed, waitForAllExpectedCommands);
        this.mockServerThread.start();
    }

    public void shutdown() {
        if (mockServerThread != null) {
            mockServerThread.shouldStop();
            try {
                mockServerThread.join(500);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
    }

    private class MockServerThread extends Thread {
        private final ServerSocket serverSocket;
        private volatile boolean running = true;

        MockServerThread(ServerSocket serverSocket, Deque<ImapInteraction> interactions, CountDownLatch waitForConnectionClosed, CountDownLatch waitForAllExpectedCommands) {
            this.serverSocket = serverSocket;
        }

        public void run() {
            try (Socket clientSocket = serverSocket.accept()) {
                handleInteractions(clientSocket);
                readAdditionalCommands(clientSocket);
                waitForConnectionClosed.countDown();
            } catch (IOException e) {
                if (running) {
                    e.printStackTrace();
                }
            } finally {
                try {
                    serverSocket.close();
                } catch (IOException ignored) {}
            }
        }

        private void handleInteractions(Socket clientSocket) throws IOException {
            BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(clientSocket.getOutputStream()));
            BufferedReader reader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
            for (ImapInteraction interaction : interactions) {
                writer.write(interaction.getServerResponse() + "\r\n");
                writer.flush();
                String command = reader.readLine();
                interaction.checkCommand(command);
            }
            waitForAllExpectedCommands.countDown();
        }

        private void readAdditionalCommands(Socket clientSocket) throws IOException {
            BufferedReader reader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
            while (running && reader.ready()) {
                reader.readLine();
            }
        }

        void shouldStop() {
            running = false;
            try {
                if (!serverSocket.isClosed()) {
                    serverSocket.close();
                }
            } catch (IOException ignored) {}
