                    byteArrayOutputStream.write(buffer, 0, bytesRead);
                }
                byte[] responseBytes = byteArrayOutputStream.toByteArray();
                return new OCSPResp(responseBytes);
            } catch (Exception e) {
                throw new IOException("Failed to parse OCSP response", e);
