import io.fabric8.kubernetes.client.KubernetesClient;
import io.fabric8.kubernetes.client.utils.PortForward;
import io.fabric8.kubernetes.client.Kubectl;
import java.io.*;
import java.net.ServerSocket;
import java.net.Socket;
import java.util.ArrayList;
import java.util.List;
import java.util.function.Consumer;

public class KubectlPortForward extends Kubectl.ResourceAndContainerBuilder<KubectlPortForward> implements Kubectl.Executable<Boolean> {

    private final List<Integer> localPorts = new ArrayList<>();
    private final List<Integer> targetPorts = new ArrayList<>();
    private volatile boolean running = false;
    private Consumer<Throwable> onUnhandledError = throwable -> {};
    private final List<Thread> activeThreads = new ArrayList<>();
    private final List<ServerSocket> serverSockets = new ArrayList<>();

    public KubectlPortForward ports(int localPort, int targetPort) {
        localPorts.add(localPort);
        targetPorts.add(targetPort);
        return this;
    }

    public KubectlPortForward onUnhandledError(Consumer<Throwable> onUnhandledError) {
        this.onUnhandledError = onUnhandledError;
        return this;
    }

    @Override
    public Boolean execute() {
        try {
            executeInternal();
            return true;
        } catch (Exception e) {
            onUnhandledError.accept(e);
            return false;
        }
    }

    private void executeInternal() throws Exception {
        running = true;
        String namespace = getNamespace();
        String podName = getPodName();
        KubernetesClient client = getKubernetesClient();

        PortForward pf = new PortForward(client, namespace, podName, targetPorts);
        serverSockets.clear();
        activeThreads.clear();

        try {
            for (int i = 0; i < localPorts.size(); i++) {
                int localPort = localPorts.get(i);
                int targetPort = targetPorts.get(i);
                ServerSocket serverSocket = new ServerSocket(localPort);
                serverSockets.add(serverSocket);
                Thread thread = portForward(pf, serverSocket, targetPort);
                activeThreads.add(thread);
                thread.start();
            }

            for (Thread thread : activeThreads) {
                thread.join();
            }
        } catch (IOException | InterruptedException e) {
            onUnhandledError.accept(e);
            throw e;
        }
    }

    private Thread portForward(PortForward pf, ServerSocket server, int targetPort) {
        return new Thread(() -> {
            try {
                while (running) {
                    Socket clientSocket = server.accept();
                    if (!running) {
                        clientSocket.close();
                        break;
                    }
                    PortForward.ForwardedPort forwardedPort = pf.forwardLocalPortToPort(targetPort);
                    InputStream clientInput = clientSocket.getInputStream();
                    OutputStream clientOutput = clientSocket.getOutputStream();
                    InputStream serverInput = forwardedPort.getInputStream();
                    OutputStream serverOutput = forwardedPort.getOutputStream();

                    Thread toServer = copyAsync(clientInput, serverOutput, onUnhandledError);
                    Thread toClient = copyAsync(serverInput, clientOutput, onUnhandledError);
                    activeThreads.add(toServer);
                    activeThreads.add(toClient);
                    toServer.start();
                    toClient.start();
                }
            } catch (IOException | PortForward.ApiException e) {
                onUnhandledError.accept(e);
            }
        });
    }

    private Thread copyAsync(InputStream input, OutputStream output, Consumer<Throwable> onError) {
        return new Thread(() -> {
            try {
                byte[] buffer = new byte[8192];
                int len;
                while (running && (len = input.read(buffer)) != -1) {
                    output.write(buffer, 0, len);
                }
            } catch (IOException e) {
                onError.accept(e);
            } finally {
                try {
                    input.close();
                    output.close();
                } catch (IOException e) {
                    onError.accept(e);
                }
            }
        });
    }

    public void shutdown() {
        running = false;
        for (Thread thread : activeThreads) {
            thread.interrupt();
        }
        activeThreads.clear();
        for (ServerSocket ss : serverSockets) {
            try {
                ss.close();
            } catch (IOException e) {
                onUnhandledError.accept(e);
            }
        }
        serverSockets.clear();
    }
}

