import java.io.*;
import java.net.*;
import java.util.logging.Logger;

public class TcpTransport {
    private ServerSocket srv_sock;
    private Socket sock;
    private OutputStream output;
    private InputStream input;
    private Thread receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private int out_buf_size = 8192;
    private int in_buf_size = 8192;
    private boolean server;
    private boolean tcp_nodelay;
    private final Logger log = Logger.getLogger(TcpTransport.class.getName());

    public void start(String... options) {
        for (String opt : options) {
            if (opt.equals("server")) {
                server = true;
            } else if (opt.startsWith("host=")) {
                try {
                    host = InetAddress.getByName(opt.substring(5));
                } catch (UnknownHostException e) {
                    log.severe("Invalid host: " + e.getMessage());
                }
            } else if (opt.startsWith("port=")) {
                port = Integer.parseInt(opt.substring(5));
            } else if (opt.equals("tcpnodelay")) {
                tcp_nodelay = true;
            } else if (opt.startsWith("inbuf=")) {
                in_buf_size = Integer.parseInt(opt.substring(6));
            } else if (opt.startsWith("outbuf=")) {
                out_buf_size = Integer.parseInt(opt.substring(7));
            }
        }

        try {
            if (server) {
                srv_sock = new ServerSocket(port);
                srv_sock.setReuseAddress(true);
                sock = srv_sock.accept();
            } else {
                sock = new Socket();
                sock.connect(new InetSocketAddress(host, port));
            }

            sock.setTcpNoDelay(tcp_nodelay);
            sock.setSoTimeout(0);
            sock.setReceiveBufferSize(in_buf_size);
            sock.setSendBufferSize(out_buf_size);

            output = sock.getOutputStream();
            input = sock.getInputStream();

            receiver_thread = new Thread(new Receiver());
            receiver_thread.start();
        } catch (IOException e) {
            log.severe("Failed to start transport: " + e.getMessage());
            stop();
        }
    }

    public void stop() {
        try {
            if (receiver_thread != null) {
                receiver_thread.interrupt();
                receiver_thread = null;
            }
            if (output != null) output.close();
            if (input != null) input.close();
            if (sock != null) sock.close();
            if (srv_sock != null) srv_sock.close();
        } catch (IOException e) {
            log.severe("Error stopping transport: " + e.getMessage());
        }
    }

    public void send(byte[] buf, int offset, int length) {
        try {
            output.write(buf, offset, length);
            output.flush();
        } catch (IOException e) {
            log.severe("Failed to send data: " + e.getMessage());
            stop();
        }
    }

    public void setReceiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    private class Receiver implements Runnable {
        @Override
        public void run() {
            byte[] buffer = new byte[8192];
            int bytesRead;
            try {
                while ((bytesRead = input.read(buffer)) != -1) {
                    if (receiver != null) {
                        receiver.onReceive(buffer, 0, bytesRead);
                    }
                }
            } catch (IOException e) {
                log.severe("Error receiving data: " + e.getMessage());
            } finally {
                stop();
            }
        }
    }

    public interface RtReceiver {
        void onReceive(byte[] data, int offset, int length);
    }
}

