import java.io.*;
import java.net.*;
import java.nio.charset.StandardCharsets;

public class HttpMediaStreamer {
    private URI uri;
    private ServerSocket serverSocket;
    private static final String TAG = "HttpMediaStreamer";

    public HttpMediaStreamer(String filename, String mimeType) {
        try {
            create(filename, mimeType);
        } catch (IOException | URISyntaxException e) {
            throw new RuntimeException(e);
        }
    }

    public URI getUri() {
        return uri;
    }

    public void destroy() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
            System.err.println(TAG + ": Error closing server socket: " + e.getMessage());
        }
    }

    private URI create(String filename, String mimeType) throws IOException, URISyntaxException {
        serverSocket = new ServerSocket(0);
        int port = serverSocket.getLocalPort();
        uri = new URI("http", null, "localhost", port, "/", null, null);

        new Thread(() -> {
            try {
                while (!serverSocket.isClosed()) {
                    try {
                        Socket clientSocket = serverSocket.accept();
                        new Thread(new ClientHandler(clientSocket, filename, mimeType)).start();
                    } catch (IOException e) {
                        if (!serverSocket.isClosed()) {
                            System.err.println(TAG + ": Error accepting connection: " + e.getMessage());
                        }
                    }
                }
            } catch (Exception e) {
                System.err.println(TAG + ": Server thread error: " + e.getMessage());
            }
        }).start();

        return uri;
    }

    private static class ClientHandler implements Runnable {
        private final Socket clientSocket;
        private final String filename;
        private final String mimeType;

        public ClientHandler(Socket socket, String filename, String mimeType) {
            this.clientSocket = socket;
            this.filename = filename;
            this.mimeType = mimeType;
        }

        @Override
        public void run() {
            try {
                BufferedReader reader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                String requestLine = reader.readLine();

                if (requestLine == null) {
                    clientSocket.close();
                    return;
                }

                String[] parts = requestLine.split(" ");
                if (parts.length < 3) {
                    sendErrorResponse(clientSocket, 400, "Bad Request");
                    return;
                }

                String method = parts[0];
                String path = parts[1];
                String version = parts[2];

                if (!method.equals("GET") || !path.equals("/") || !version.startsWith("HTTP/")) {
                    sendErrorResponse(clientSocket, 405, "Method Not Allowed");
                    return;
                }

                File file = new File(filename);
                if (!file.exists()) {
                    sendErrorResponse(clientSocket, 404, "File Not Found");
                    return;
                }

                String headers = "HTTP/1.1 200 OK\r\n" +
                               "Content-Type: " + mimeType + "\r\n" +
                               "Content-Length: " + file.length() + "\r\n" +
                               "Connection: close\r\n" +
                               "\r\n";
                OutputStream out = clientSocket.getOutputStream();
                out.write(headers.getBytes(StandardCharsets.ISO_8859_1));

                try (FileInputStream fileIn = new FileInputStream(file)) {
                    byte[] buffer = new byte[4096];
                    int bytesRead;
                    while ((bytesRead = fileIn.read(buffer)) != -1) {
                        out.write(buffer, 0, bytesRead);
                    }
                }
                out.flush();
            } catch (IOException e) {
                System.err.println(TAG + ": Error handling client: " + e.getMessage());
            } finally {
                try {
                    clientSocket.close();
                } catch (IOException e) {
                    System.err.println(TAG + ": Error closing client socket: " + e.getMessage());
                }
            }
        }

        private void sendErrorResponse(Socket socket, int code, String message) throws IOException {
            String response = "HTTP/1.1 " + code + " " + message + "\r\n" +
                             "Content-Length: 0\r\n" +
                             "Connection: close\r\n" +
                             "\r\n";
            OutputStream out = socket.getOutputStream();
            out.write(response.getBytes(StandardCharsets.ISO_8859_1));
            out.flush();
            socket.close();
        }
    }
}

