import javax.net.ssl.*;

public class EchoServer extends Thread {
    private final int port;
    private ServerSocket serverSocket;
    private final List<Thread> threads = Collections.synchronizedList(new ArrayList<>());
    private final List<Socket> sockets = Collections.synchronizedList(new ArrayList<>());
    private final List<Exception> exceptions = Collections.synchronizedList(new ArrayList<>());
    private final AtomicBoolean renegotiate = new AtomicBoolean(false);

    public EchoServer(int port) throws IOException {
        this.port = port;
        this.serverSocket = new ServerSocket(port);
    }

    public EchoServer(SSLFactory sslFactory, int port) throws IOException {
        this.port = port;
        this.serverSocket = sslFactory.createServerSocket(port);
    }

    public void renegotiate() {
        this.renegotiate.set(true);
    }

    @Override
    public void run() {
        try {
            while (!Thread.interrupted()) {
                Socket clientSocket = serverSocket.accept();
                synchronized (sockets) {
                    sockets.add(clientSocket);
                }
                Thread handler = new Thread(new ClientHandler(clientSocket));
                threads.add(handler);
                handler.start();
            }
        } catch (IOException e) {
            exceptions.add(e);
        }
    }

    private class ClientHandler implements Runnable {
        private final Socket socket;

        public ClientHandler(Socket socket) {
            this.socket = socket;
        }

        @Override
        public void run() {
            try {
                InputStream input = socket.getInputStream();
                OutputStream output = socket.getOutputStream();
                byte[] buffer = new byte[1024];
                int bytesRead;

                while ((bytesRead = input.read(buffer)) != -1) {
                    output.write(buffer, 0, bytesRead);
                    if (renegotiate.get() && socket instanceof SSLSocket) {
                        ((SSLSocket) socket).startHandshake();
                    }
                }
            } catch (Exception e) {
                exceptions.add(e);
            } finally {
                try {
                    socket.close();
                } catch (IOException e) {
                    exceptions.add(e);
                }
                synchronized (sockets) {
                    sockets.remove(socket);
                }
                threads.remove(Thread.currentThread());
            }
        }
    }

    public void closeConnections() {
        for (Socket socket : new ArrayList<>(sockets)) {
            try {
                socket.close();
            } catch (IOException e) {
                exceptions.add(e);
            }
        }
        sockets.clear();
    }

    public void close() {
        try {
            serverSocket.close();
        } catch (IOException e) {
            exceptions.add(e);
        }
        closeConnections();
        interrupt();
    }

    public int getExceptionCount() {
        return exceptions.size();
    }
}

