                port = Integer.parseInt(args[++i]);
            } else if (args[i].equals("-mech")) {
                if (i + 1 >= args.length) {
                    usage();
                    System.exit(1);
                }
                try {
                    mechOid = new Oid(args[++i]);
                } catch (Exception e) {
                    System.err.println("Invalid mech OID: " + args[i]);
                    System.exit(1);
                }
            } else if (serviceName == null) {
                serviceName = args[i];
            } else {
                usage();
                System.exit(1);
            }
        }
        if (serviceName == null) {
            usage();
            System.exit(1);
        }
        GSSManager manager = GSSManager.getInstance();
        try {
            GSSName serverName = manager.createName(serviceName, null, null);
            GSSCredential serverCred = manager.createCredential(
                serverName, GSSCredential.INDEFINITE_LIFETIME, mechOid, GSSCredential.ACCEPT_ONLY);
            GSSServer server = new GSSServer();
            server.s = new ServerSocket(port);
            print("Server started on port " + port);
            while (true) {
                Socket client = server.s.accept();
                print("Client connected: " + client.getInetAddress());
                new Thread(() -> {
                    try {
                        processClient(serverCred, client);
                    } catch (Exception e) {
                        e.printStackTrace();
                    }
                }).start();
            }
        } catch (Exception e) {
            e.printStackTrace();
            System.exit(1);
        }
    }

    private static void processClient(GSSCredential serverCred, Socket client) throws IOException, GSSException {
        DataInputStream dis = new DataInputStream(client.getInputStream());
        DataOutputStream dos = new DataOutputStream(client.getOutputStream());
        GSSManager manager = GSSManager.getInstance();
        GSSContext context = manager.createContext(serverCred);
        try {
            while (!context.isEstablished()) {
                int tokenLen = dis.readInt();
                byte[] inToken = new byte[tokenLen];
                dis.readFully(inToken);
                byte[] outToken = context.acceptSecContext(inToken, 0, inToken.length);
                if (outToken != null) {
                    dos.writeInt(outToken.length);
                    dos.write(outToken);
                    dos.flush();
                }
            }
            print("Security context established with " + context.getSrcName());
            exchangeWithPeer(context, dis, dos);
        } finally {
            context.dispose();
            client.close();
        }
    }

    private static void exchangeWithPeer(GSSContext aCtxt, DataInputStream dis, DataOutputStream dos) throws IOException, GSSException {
        int len = dis.readInt();
        byte[] inToken = new byte[len];
        dis.readFully(inToken);
        MessageInfo msgInfo = aCtxt.unwrap(inToken, 0, inToken.length);
        String clientMsg = new String(msgInfo.getBytes(), "UTF-8");
        print("Received from client: " + clientMsg);
        byte[] response = "Hello from server".getBytes("UTF-8");
        byte[] outToken = aCtxt.wrap(response, 0, response.length, GSSContext.WRAP_FLAG_CONFIDENTIALITY | GSSContext.WRAP_FLAG_INTEGRITY);
        dos.writeInt(outToken.length);
        dos.write(outToken);
