                    new Thread(() -> handleClientConnection(client)).start();
                }
            } catch (IOException e) {
                if (running) {
                    e.printStackTrace();
                }
            } finally {
                try {
                    if (serverSocket != null && !serverSocket.isClosed()) {
                        serverSocket.close();
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        });
        serverThread.start();
    }

    public void stop() {
        running = false;
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        if (serverThread != null) {
            serverThread.interrupt();
        }
    }

    private void handleClientConnection(Socket client) {
        try {
            BufferedReader reader = new BufferedReader(new InputStreamReader(client.getInputStream()));
            BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(client.getOutputStream()));
            StringBuilder request = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null && !line.isEmpty()) {
                request.append(line).append("\r\n");
            }
            String secWebSocketKey = null;
            for (String headerLine : request.toString().split("\r\n")) {
                if (headerLine.startsWith("Sec-WebSocket-Key:")) {
                    secWebSocketKey = headerLine.substring("Sec-WebSocket-Key:".length()).trim();
                    break;
                }
            }
            if (secWebSocketKey == null) {
                writer.write("HTTP/1.1 400 Bad Request\r\n\r\n");
                writer.flush();
                return;
            }
            String acceptKey = generateFinalKey(secWebSocketKey);
            writer.write("HTTP/1.1 101 Switching Protocols\r\n");
            writer.write("Upgrade: websocket\r\n");
            writer.write("Connection: Upgrade\r\n");
            writer.write("Sec-WebSocket-Accept: " + acceptKey + "\r\n");
            writer.write("\r\n");
            writer.flush();
        } catch (IOException e) {
            e.printStackTrace();
            try {
                client.close();
            } catch (IOException ex) {
                ex.printStackTrace();
            }
        }
    }

    private String generateFinalKey(String in) {
        try {
            String guid = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
            MessageDigest digest = MessageDigest.getInstance("SHA-1");
            byte[] hash = digest.digest((in + guid).getBytes(StandardCharsets.UTF_8));
