public class WebSocketServer {
    private ServerSocket serverSocket;
    private int port;
    private boolean isMaskingEnabled = false;
    private int frameSize = 1024;

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void setMaskingEnabled(boolean isMaskingEnabled) {
        this.isMaskingEnabled = isMaskingEnabled;
    }

    public void setFrameSize(int frameSize) {
        this.frameSize = frameSize;
    }

    public void start() {
        new Thread(() -> {
            try {
                serverSocket = new ServerSocket(port);
                while (!serverSocket.isClosed()) {
                    Socket clientSocket = serverSocket.accept();
                    new Thread(() -> handleConnection(clientSocket)).start();
                }
            } catch (IOException e) {
                if (!serverSocket.isClosed()) {
                    e.printStackTrace();
                }
            }
        }).start();
    }

    private void handleConnection(Socket clientSocket) {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
             OutputStream out = clientSocket.getOutputStream()) {
            StringBuilder headers = new StringBuilder();
            String line;
            while (!(line = reader.readLine()).isEmpty()) {
                headers.append(line).append("\r\n");
            }
            String secWebSocketKey = null;
            for (String header : headers.toString().split("\r\n")) {
                if (header.startsWith("Sec-WebSocket-Key: ")) {
                    secWebSocketKey = header.substring("Sec-WebSocket-Key: ".length()).trim();
                    break;
                }
            }
            if (secWebSocketKey == null) {
                return;
            }
            String guid = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
            String combined = secWebSocketKey + guid;
            MessageDigest digest = MessageDigest.getInstance("SHA-1");
            byte[] hash = digest.digest(combined.getBytes(java.nio.charset.StandardCharsets.ISO_8859_1));
            String acceptKey = Base64.getEncoder().encodeToString(hash);
            String response = "HTTP/1.1 101 Switching Protocols\r\n" +
                    "Upgrade: websocket\r\n" +
                    "Connection: Upgrade\r\n" +
                    "Sec-WebSocket-Accept: " + acceptKey + "\r\n\r\n";
            out.write(response.getBytes(java.nio.charset.StandardCharsets.ISO_8859_1));

            ByteArrayOutputStream frame = new ByteArrayOutputStream();
            int payloadLength = frameSize;
            boolean mask = isMaskingEnabled;
            frame.write(0x82); 

            if (payloadLength <= 125) {
                frame.write((mask ? 0x80 : 0x00) | payloadLength);
            } else if (payloadLength <= 65535) {
                frame.write((mask ? 0x80 : 0x00) | 126);
                frame.write((payloadLength >> 8) & 0xFF);
                frame.write(payloadLength & 0xFF);
            } else {
                frame.write((mask ? 0x80 : 0x00) | 127);
                for (int i = 7; i >= 0; i--) {
                    frame.write((payloadLength >> (i * 8)) & 0xFF);
                }
            }

            if (mask) {
                byte[] maskingKey = new byte[4];
                new Random().nextBytes(maskingKey);
                frame.write(maskingKey);
                byte[] payload = new byte[frameSize];
                new Random().nextBytes(payload);
                for (int i = 0; i < payload.length; i++) {
                    frame.write(payload[i] ^ maskingKey[i % 4]);
                }
            } else {
                byte[] payload = new byte[frameSize];
                new Random().nextBytes(payload);
                frame.write(payload);
            }

            out.write(frame.toByteArray());
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            try {
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public void stop() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
