                    headers.put(parts[0], parts[1]);
                }
            }

            if ("/success".equals(path)) {
                if (!"GET".equals(method)) {
                    sendErrorResponse(output, 400);
                    return;
                }

                String secWebSocketKey = headers.get("Sec-WebSocket-Key");
                if (secWebSocketKey == null) {
                    sendErrorResponse(output, 400);
                    return;
                }

                try {
                    String accept = generateAcceptKey(secWebSocketKey);
                    String response = "HTTP/1.1 101 Switching Protocols\r\n" +
                            ADDITIONAL_HANDSHAKE_HEADERS +
                            "Sec-WebSocket-Accept: " + accept + "\r\n\r\n";
                    output.write(asciiBytes(response));
                } catch (NoSuchAlgorithmException e) {
                    sendErrorResponse(output, 500);
                }
            } else if ("/badrequest".equals(path)) {
                sendErrorResponse(output, 400);
            } else if ("/notfound".equals(path)) {
                sendErrorResponse(output, 404);
            } else {
                sendErrorResponse(output, 400);
            }

        } catch (IOException | InterruptedException e) {
            e.printStackTrace();
        } finally {
            try {
                client.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void sendErrorResponse(OutputStream output, int statusCode) throws IOException {
        String statusMessage;
        switch (statusCode) {
            case 400: statusMessage = "Bad Request"; break;
            case 404: statusMessage = "Not Found"; break;
            default: statusMessage = "Error";
        }
        String response = "HTTP/1.1 " + statusCode + " " + statusMessage + "\r\n" +
                "Content-Length: 0\r\n\r\n";
        output.write(asciiBytes(response));
    }

    private String generateAcceptKey(String key) throws NoSuchAlgorithmException {
        String combined = key + "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        MessageDigest digest = MessageDigest.getInstance("SHA-1");
        byte[] sha1Hash = digest.digest(combined.getBytes(StandardCharsets.ISO_8859_1));
        return Base64.getEncoder().encodeToString(sha1Hash);
    }

    private byte[] asciiBytes(String input) {
        return input.getBytes(StandardCharsets.US_ASCII);
    }
}
