        private final OutputStream output;
        private final byte[] readBuffer;
        private final byte[] writeBuffer;

        public BioConnection(Socket socket) {
            this.socket = socket;
            try {
                this.input = socket.getInputStream();
                this.output = socket.getOutputStream();
