import java.io.*;
import java.net.*;
import java.util.concurrent.atomic.AtomicReference;

public class BinaryLinkServer {
    private final Acceptor acceptor;
    private final AtomicReference<Handler> handler = new AtomicReference<>();

    public BinaryLinkServer() {
        this.acceptor = new Acceptor();
        acceptor.start();
    }

    public void terminate() {
        try {
            acceptor.close();
            acceptor.interrupt();
            Handler currentHandler = handler.get();
            if (currentHandler != null) {
                currentHandler.close();
                currentHandler.interrupt();
            }
        } catch (Exception e) {
        }
    }

    private InetAddress getListenAddress() {
        String addressProp = System.getProperty("server.address");
        if (addressProp != null) {
            try {
                return InetAddress.getByName(addressProp);
            } catch (UnknownHostException e) {
            }
        }
        return InetAddress.getLoopbackAddress();
    }

    private int getListenPort() {
        String portProp = System.getProperty("server.port");
        if (portProp != null) {
            try {
                return Integer.parseInt(portProp);
            } catch (NumberFormatException e) {
            }
        }
        return 0;
    }

    class Acceptor extends Thread {
        private ServerSocket server;
        private final InetAddress listenAddress;

        Acceptor() {
            try {
                listenAddress = getListenAddress();
                int port = getListenPort();
                server = new ServerSocket(port, 0, listenAddress);
            } catch (IOException e) {
                throw new RuntimeException("Failed to create server socket", e);
            }
        }

        @Override
        public void run() {
            try {
                while (!Thread.currentThread().isInterrupted()) {
                    Socket clientSocket = server.accept();
                    Handler oldHandler = handler.get();
                    if (oldHandler != null) {
                        oldHandler.close();
                        oldHandler.interrupt();
                    }
                    Handler newHandler = new Handler(clientSocket);
                    handler.set(newHandler);
                    newHandler.start();
                }
            } catch (IOException e) {
                if (!Thread.currentThread().isInterrupted()) {
                    e.printStackTrace();
                }
            }
        }

        void close() {
            try {
                server.close();
            } catch (IOException e) {
            }
        }
    }

    class Handler extends Thread {
        private final Socket socket;
        private InputStream is;
        private OutputStream os;
        private ObjectInputStream ois;
        private ObjectOutputStream oos;

        Handler(Socket socket) {
            this.socket = socket;
            try {
                os = socket.getOutputStream();
                oos = new ObjectOutputStream(os);
                is = socket.getInputStream();
                ois = new ObjectInputStream(is);
            } catch (IOException e) {
                throw new RuntimeException("Failed to initialize streams", e);
            }
        }

        @Override
        public void run() {
            try {
                while (!Thread.currentThread().isInterrupted()) {
                    Object obj = ois.readObject();
                    oos.writeObject(obj);
                    oos.flush();
                }
            } catch (Exception e) {
            } finally {
                close();
            }
        }

        void close() {
            try {
                if (ois != null) ois.close();
                if (oos != null) oos.close();
                if (is != null) is.close();
                if (os != null) os.close();
                if (socket != null) socket.close();
            } catch (IOException e) {
            }
        }
    }
}

