import java.io.*;
import java.net.*;
import java.util.Map;
import java.util.zip.GZIPInputStream;
import java.nio.charset.StandardCharsets;

public class JavaHttpUtility {
    public static final int CONNECT_TIMEOUT = 10000;
    public static final int READ_TIMEOUT = 10000;

    public static Proxy getProxy() {
        String proxyHost = System.getProperty("http.proxyHost");
        String proxyPortStr = System.getProperty("http.proxyPort");
        if (proxyHost != null && proxyPortStr != null) {
            int proxyPort;
            try {
                proxyPort = Integer.parseInt(proxyPortStr);
            } catch (NumberFormatException e) {
                return Proxy.NO_PROXY;
            }
            return new Proxy(Proxy.Type.HTTP, new InetSocketAddress(proxyHost, proxyPort));
        } else {
            return Proxy.NO_PROXY;
        }
    }

    public static String doPost(String urlAddress, Map<String, String> params) {
        HttpURLConnection conn = null;
        try {
            URL url = new URL(urlAddress);
            conn = (HttpURLConnection) url.openConnection(getProxy());
            conn.setConnectTimeout(CONNECT_TIMEOUT);
            conn.setReadTimeout(READ_TIMEOUT);
            conn.setRequestMethod("POST");
            conn.setDoOutput(true);
            conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");

            StringBuilder postData = new StringBuilder();
            for (Map.Entry<String, String> param : params.entrySet()) {
                if (postData.length() > 0) postData.append('&');
                postData.append(java.net.URLEncoder.encode(param.getKey(), "UTF-8"));
                postData.append('=');
                postData.append(java.net.URLEncoder.encode(param.getValue(), "UTF-8"));
            }
            byte[] postDataBytes = postData.toString().getBytes("UTF-8");

            try (OutputStream os = conn.getOutputStream()) {
                os.write(postDataBytes);
            }

            return handleResponse(conn);
        } catch (IOException e) {
            return "IOException: " + e.getMessage();
        } catch (Exception e) {
            return "Error: " + e.getMessage();
        } finally {
            if (conn != null) {
                conn.disconnect();
            }
        }
    }

    private static String handleResponse(HttpURLConnection conn) {
        try {
            int responseCode = conn.getResponseCode();
            if (responseCode >= 200 && responseCode < 300) {
                return readResult(conn);
            } else {
                return handleError(conn);
            }
        } catch (IOException e) {
            return "Error during response handling: " + e.getMessage();
        }
    }

    private static String readResult(HttpURLConnection urlConnection) throws IOException {
        InputStream inputStream = urlConnection.getInputStream();
        String contentEncoding = urlConnection.getContentEncoding();
        if (contentEncoding != null && contentEncoding.equalsIgnoreCase("gzip")) {
            inputStream = new GZIPInputStream(inputStream);
        }
        return readStream(inputStream);
    }

    private static String handleError(HttpURLConnection urlConnection) {
        try {
            InputStream errorStream = urlConnection.getErrorStream();
            if (errorStream == null) {
                return "No error stream available.";
            }
            String contentEncoding = urlConnection.getContentEncoding();
            if (contentEncoding != null && contentEncoding.equalsIgnoreCase("gzip")) {
                byte[] errorBytes = toBytes(errorStream);
                errorStream = new ByteArrayInputStream(errorBytes);
                errorStream = new GZIPInputStream(errorStream);
            }
            return readStream(errorStream);
        } catch (IOException e) {
            return "Error reading error stream: " + e.getMessage();
        }
    }

    private static String readStream(InputStream inputStream) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream, StandardCharsets.UTF_8));
        StringBuilder response = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            response.append(line).append("\n");
        }
        return response.toString().trim();
    }

    private static byte[] toBytes(InputStream is) throws IOException {
        ByteArrayOutputStream buffer = new ByteArrayOutputStream();
        int nRead;
        byte[] data = new byte[16384];
        while ((nRead = is.read(data, 0, data.length)) != -1) {
            buffer.write(data, 0, nRead);
        }
        buffer.flush();
        return buffer.toByteArray();
    }
}
