import java.util.HashMap;
import java.util.Map;

public abstract class EmbedHttpServer {
    private int port;
    private ServerSocket serverSocket;
    private Thread serverThread;
    private volatile boolean running;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        running = true;
        serverThread = new Thread(this::run);
        serverThread.start();
    }

    public void stop() {
        running = false;
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void run() {
        try {
            while (running) {
                Socket socket = serverSocket.accept();
                handleRequest(socket);
            }
        } catch (IOException e) {
            if (running) {
                e.printStackTrace();
            }
        } finally {
            try {
                if (serverSocket != null) {
                    serverSocket.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void handleRequest(Socket socket) {
        try {
            BufferedReader reader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
            OutputStream responseOutputStream = socket.getOutputStream();

            String requestLine = reader.readLine();
            if (requestLine == null) {
                return;
            }
            String[] requestParts = requestLine.split(" ", 3);
            if (requestParts.length < 2) {
                return;
            }
            String method = requestParts[0];
            String originalPath = requestParts[1];

            Map<String, String> headers = new HashMap<>();
            String headerLine;
            while ((headerLine = reader.readLine()) != null && !headerLine.trim().isEmpty()) {
                int colonIndex = headerLine.indexOf(':');
                if (colonIndex != -1) {
                    String key = headerLine.substring(0, colonIndex).trim();
                    String value = headerLine.substring(colonIndex + 1).trim();
                    headers.put(key, value);
                }
            }

            String[] pathSplit = originalPath.split("\\?", 2);
            String path = pathSplit[0];
            Map<String, String> queries = parsePath(originalPath);

            handle(method, path, headers, queries, socket.getInputStream(), responseOutputStream);
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                socket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    protected abstract void handle(String method, String path, Map<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response);

    protected Map<String, String> parsePath(String path) {
        Map<String, String> queries = new HashMap<>();
        int qmarkIndex = path.indexOf('?');
        if (qmarkIndex != -1) {
            String queryPart = path.substring(qmarkIndex + 1);
            String[] pairs = queryPart.split("&");
            for (String pair : pairs) {
                int equalsIndex = pair.indexOf('=');
                String key, value;
                if (equalsIndex == -1) {
                    key = pair;
                    value = "";
                } else {
                    key = pair.substring(0, equalsIndex);
                    value = pair.substring(equalsIndex + 1);
                }
                queries.put(key, value);
            }
        }
        return queries;
    }
}
