            if (parts.length != 3 || !parts[0].equals("GET")) {
                replyError(out, new BadHttpRequest("Unsupported method"));
                return;
            }
            String filename = parts[1];
            doReply(in, out, "GET", filename);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    protected String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\r') {
                in.read(); // skip \n
                break;
            }
            sb.append((char) c);
        }
        return sb.toString();
    }

    protected int skipLine(InputStream in) throws IOException {
        int count = 0;
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\n') {
                count++;
                break;
            } else if (c == '\r') {
                count++;
                c = in.read();
                if (c != '\n') {
                    in.unread(c);
                    break;
                }
            } else {
                count++;
            }
        }
        return count;
    }

    protected void doReply(InputStream in, OutputStream out, String cmd, String filename) throws IOException, CannotCompileException {
        if (!cmd.equals("GET")) {
            replyError(out, new BadHttpRequest("Method not supported"));
            return;
        }

        boolean handled = false;
        if (filename.endsWith(".class")) {
            handled = letUsersSendClassfile(out, filename);
        }

        if (!handled) {
            File file = new File(filename.startsWith("/") ? filename : "/" + filename);
            if (!file.exists()) {
                replyError(out, new BadHttpRequest("File not found"));
                return;
            }

            if (file.isDirectory()) {
                replyError(out, new BadHttpRequest("Directory access forbidden"));
                return;
            }

            int fileType = 0;
            if (filename.endsWith(".html")) fileType = 0;
            else if (filename.endsWith(".css")) fileType = 1;
            else if (filename.endsWith(".js")) fileType = 2;
            else fileType = 3;

            sendHeader(out, file.length(), fileType);

            try (FileInputStream fis = new FileInputStream(file)) {
                byte[] buffer = new byte[4096];
                int bytesRead;
                while ((bytesRead = fis.read(buffer)) != -1) {
                    out.write(buffer, 0, bytesRead);
                }
            }
        }
    }

    protected boolean letUsersSendClassfile(OutputStream out, String filename) throws IOException, CannotCompileException {
        if (classPool == null) {
            return false;
        }
        String className = filename.substring(1, filename.length() - 6).replace('/', '.');
        try {
            CtClass ctClass = classPool.get(className);
            byte[] bytecode = ctClass.toBytecode();
            sendHeader(out, bytecode.length, 3);
            out.write(bytecode);
            out.flush();
            return true;
        } catch (NotFoundException | IOException | CannotCompileException e) {
            return false;
        }
    }

    protected void sendHeader(OutputStream out, long dataLength, int filetype) throws IOException {
        String contentType;
        switch (filetype) {
            case 0: contentType = "text/html"; break;
            case 1: contentType = "text/css"; break;
            case 2: contentType = "application/javascript"; break;
            case 3: contentType = "application/octet-stream"; break;
            default: contentType = "text/plain"; break;
        }
        out.write("HTTP/1.0 200 OK\r\n".getBytes());
        out.write(("Content-Type: " + contentType + "\r\n").getBytes());
        out.write(("Content-Length: " + dataLength + "\r\n").getBytes());
        out.write("Connection: close\r\n\r\n".getBytes());
        out.flush();
    }

    protected void replyError(OutputStream out, BadHttpRequest e) throws IOException {
        sendHeader(out, 0, 0);
        out.write("HTTP/1.0 400 Bad Request\r\n\r\n".getBytes());
        out.write(e.getMessage().getBytes());
        out.flush();
    }

    static class BadHttpRequest extends Exception {
        BadHttpRequest(String msg) {
            super(msg);
        }
    }
}

