abstract public class EmbedHttpServer implements Runnable {
    private int port;
    private ServerSocket serverSocket;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        new Thread(this).start();
    }

    public void stop() throws IOException {
        if (serverSocket != null) {
            serverSocket.close();
        }
    }

    public abstract void handle(String method, String path, HashMap<String, String> headers, InputStream input, OutputStream response);

    @Override
    public void run() {
        try {
            while (true) {
                Socket socket = serverSocket.accept();
                try {
                    BufferedReader reader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
                    OutputStream responseOutputStream = socket.getOutputStream();

                    String requestLine = reader.readLine();
                    if (requestLine == null) {
                        continue;
                    }
                    String[] requestParts = requestLine.split(" ", 3);
                    String method = requestParts[0];
                    String path = requestParts[1];

                    HashMap<String, String> headers = new HashMap<>();
                    String headerLine;
                    while ((headerLine = reader.readLine()) != null && !headerLine.isEmpty()) {
                        int colonIndex = headerLine.indexOf(':');
                        if (colonIndex != -1) {
                            String key = headerLine.substring(0, colonIndex).trim();
                            String value = headerLine.substring(colonIndex + 1).trim();
                            headers.put(key, value);
                        }
                    }

                    int contentLength = 0;
                    if (headers.containsKey("Content-Length")) {
                        try {
                            contentLength = Integer.parseInt(headers.get("Content-Length"));
                        } catch (NumberFormatException ignored) {}
                    }

                    InputStream bodyInputStream = new BodyInputStream(socket.getInputStream(), contentLength);
                    handle(method, path, headers, bodyInputStream, responseOutputStream);

                } finally {
                    socket.close();
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static class BodyInputStream extends InputStream {
        private final InputStream ins;
        private final int maxBytes;
        private int remaining;

        public BodyInputStream(InputStream ins, int n) {
            this.ins = ins;
            this.maxBytes = n;
            this.remaining = n;
        }

        @Override
        public int available() throws IOException {
            return Math.min(ins.available(), remaining);
        }

        @Override
        public int read() throws IOException {
            if (remaining <= 0) {
                return -1;
            }
            int b = ins.read();
            if (b != -1) {
                remaining--;
                return b;
            }
            return -1;
        }

        @Override
        public int read(byte[] b, int off, int len) throws IOException {
            if (remaining <= 0) {
                return -1;
            }
            len = Math.min(len, remaining);
            int bytesRead = ins.read(b, off, len);
            if (bytesRead != -1) {
                remaining -= bytesRead;
                return bytesRead;
            }
            return -1;
        }

        @Override
        public long skip(long n) throws IOException {
            throw new IOException("Skipping not supported");
        }

        @Override
        public void close() throws IOException {
            ins.close();
        }
    }
}

