import java.io.*;
import java.net.*;
import java.security.*;
import java.util.Deque;
import java.util.concurrent.CountDownLatch;
import javax.net.ssl.*;

public class MockSmtpServer {
    private final Deque<SmtpInteraction> interactions;
    private final CountDownLatch waitForConnectionClosed;
    private final CountDownLatch waitForAllExpectedCommands;
    private final KeyStoreProvider keyStoreProvider;
    private final Logger logger;
    private MockServerThread mockServerThread;
    private String host = "localhost";
    private int port;

    public MockSmtpServer(KeyStoreProvider keyStoreProvider, Logger logger) {
        this.interactions = new java.util.concurrent.LinkedBlockingDeque<>();
        this.waitForConnectionClosed = new CountDownLatch(1);
        this.waitForAllExpectedCommands = new CountDownLatch(1);
        this.keyStoreProvider = keyStoreProvider;
        this.logger = logger;
    }

    public void start() {
        try {
            ServerSocket serverSocket = new ServerSocket(0);
            this.port = serverSocket.getLocalPort();
            this.mockServerThread = new MockServerThread(serverSocket, interactions, waitForConnectionClosed, waitForAllExpectedCommands, logger, keyStoreProvider);
            mockServerThread.start();
            waitForConnectionClosed.await();
        } catch (IOException | InterruptedException e) {
            logger.log("Failed to start server: " + e.getMessage());
            Thread.currentThread().interrupt();
        }
    }

    public void shutdown() {
        if (mockServerThread != null) {
            mockServerThread.shouldStop();
            try {
                mockServerThread.join();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
    }

    private class MockServerThread extends Thread {
        private final ServerSocket serverSocket;
        private final Deque<SmtpInteraction> interactions;
        private final CountDownLatch waitForConnectionClosed;
        private final CountDownLatch waitForAllExpectedCommands;
        private final Logger logger;
        private final KeyStoreProvider keyStoreProvider;
        private volatile boolean stopped = false;

        public MockServerThread(ServerSocket serverSocket, Deque<SmtpInteraction> interactions, CountDownLatch waitForConnectionClosed, CountDownLatch waitForAllExpectedCommands, Logger logger, KeyStoreProvider keyStoreProvider) {
            this.serverSocket = serverSocket;
            this.interactions = interactions;
            this.waitForConnectionClosed = waitForConnectionClosed;
            this.waitForAllExpectedCommands = waitForAllExpectedCommands;
            this.logger = logger;
            this.keyStoreProvider = keyStoreProvider;
        }

        public void shouldStop() {
            stopped = true;
            try {
                serverSocket.close();
            } catch (IOException e) {
                logger.log("Error closing server socket: " + e.getMessage());
            }
        }

        @Override
        public void run() {
            waitForConnectionClosed.countDown();
            try {
                while (!stopped) {
                    Socket clientSocket = serverSocket.accept();
                    logger.log("Client connected: " + clientSocket.getInetAddress());
                    try {
                        processInteractions(clientSocket);
                    } finally {
                        clientSocket.close();
                        logger.log("Client disconnected");
                    }
                }
            } catch (IOException e) {
                if (!stopped) {
                    logger.log("Server socket error: " + e.getMessage());
                }
            }
        }

        private void processInteractions(Socket socket) throws IOException {
            InputStream input = socket.getInputStream();
            OutputStream output = socket.getOutputStream();
            BufferedReader reader = new BufferedReader(new InputStreamReader(input));
            BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(output));

            try {
                for (SmtpInteraction interaction : interactions) {
                    if (stopped) break;
                    if (interaction instanceof ExpectedCommand) {
                        readExpectedCommand((ExpectedCommand) interaction, reader);
                    } else if (interaction instanceof CannedResponse) {
                        writeCannedResponse((CannedResponse) interaction, writer);
                    } else if (interaction instanceof UpgradeToTls) {
                        upgradeToTls(socket);
                        input = ((SSLSocket) socket).getInputStream();
                        output = ((SSLSocket) socket).getOutputStream();
                        reader = new BufferedReader(new InputStreamReader(input));
                        writer = new BufferedWriter(new OutputStreamWriter(output));
                    }
                }
                waitForAllExpectedCommands.countDown();
            } finally {
                try {
                    writer.close();
                    reader.close();
                } catch (IOException e) {
                    logger.log("Error closing streams: " + e.getMessage());
                }
            }
        }

        private void readExpectedCommand(ExpectedCommand command, BufferedReader reader) throws IOException {
            String line = reader.readLine();
            if (line == null) {
                throw new IOException("Unexpected end of stream while reading command");
            }
            if (!line.equals(command.getExpectedCommand())) {
                throw new IOException("Unexpected command: expected " + command.getExpectedCommand() + ", got " + line);
            }
            logger.log("Received expected command: " + line);
        }

        private void writeCannedResponse(CannedResponse response, BufferedWriter writer) throws IOException {
            writer.write(response.getResponse());
            writer.newLine();
            writer.flush();
            logger.log("Sent response: " + response.getResponse());
        }

        private void upgradeToTls(Socket socket) throws IOException {
            if (!(socket instanceof SSLSocket)) {
                SSLContext sslContext = createSslContext();
                SSLSocketFactory socketFactory = sslContext.getSocketFactory();
                SSLSocket sslSocket = (SSLSocket) socketFactory.createSocket(socket, socket.getInetAddress().getHostAddress(), socket.getPort(), true);
                sslSocket.startHandshake();
                logger.log("Upgraded to TLS");
            }
        }

        private SSLContext createSslContext() throws IOException {
            try {
                KeyStore keyStore = keyStoreProvider.getKeyStore();
                String password = keyStoreProvider.getPassword();
                KeyManagerFactory keyManagerFactory = KeyManagerFactory.getInstance(KeyManagerFactory.getDefaultAlgorithm());
                keyManagerFactory.init(keyStore, password.toCharArray());
                SSLContext sslContext = SSLContext.getInstance("TLS");
                sslContext.init(keyManagerFactory.getKeyManagers(), null, null);
                return sslContext;
            } catch (Exception e) {
                throw new IOException("Failed to create SSL context: " + e.getMessage(), e);
            }
        }
    }

    public interface SmtpInteraction {}
    public static class ExpectedCommand implements SmtpInteraction {
        private final String expectedCommand;

        public ExpectedCommand(String expectedCommand) {
            this.expectedCommand = expectedCommand;
        }

        public String getExpectedCommand() {
            return expectedCommand;
        }
    }
    public static class CannedResponse implements SmtpInteraction {
        private final String response;

        public CannedResponse(String response) {
            this.response = response;
        }

        public String getResponse() {
            return response;
        }
    }
    public static class UpgradeToTls implements SmtpInteraction {}

    public interface Logger {
        void log(String message);
    }
    public interface KeyStoreProvider {
        KeyStore getKeyStore() throws KeyStoreException, NoSuchAlgorithmException, CertificateException, IOException;
        String getPassword();
    }
}
