import org.apache.commons.codec.binary.Base64;

public class SendMailJob {
    private Socket socket;
    private BufferedReader in;
    private OutputStream out;
    private Base64OutputStream out64;
    private boolean connectedToMailServer;
    private String mailServer;
    private String fromAddress;
    private String boundary;
    private String recipientString;
    private String mailSubject;
    private String mailBody;
    private String errorDialogTitle;
    private FileSet fileSet;

    public SendMailJob(Object progressDialog, Object mainFrame, FileSet files, String server, String from, String subject) {
        this.mailServer = server;
        this.fromAddress = from;
        this.mailSubject = subject;
        this.fileSet = files;
        this.recipientString = "recipient@example.com";
        this.mailBody = "Default body";
        this.boundary = "===" + System.currentTimeMillis() + "===";
        this.errorDialogTitle = "Email Error";
    }

    public void openConnection() {
        try {
            socket = new Socket(mailServer, 25);
            in = new BufferedReader(new InputStreamReader(socket.getInputStream()));
            out = socket.getOutputStream();
            out64 = new Base64OutputStream(out, Base64.ENCODE);
            connectedToMailServer = true;
            String response = in.readLine();
            if (!response.startsWith("220")) {
                throw new IOException("SMTP server didn't respond with 220");
            }
            readWriteLine("HELO " + mailServer);
            readWriteLine("MAIL FROM:<" + fromAddress + ">");
        } catch (IOException e) {
            handleError("Failed to connect to SMTP server: " + e.getMessage());
        }
    }

    public void sendBody() {
        try {
            List<String> recipientsList = new ArrayList<>();
            String error = splitRecipientString(recipientString, recipientsList);
            if (error != null && !error.isEmpty()) {
                throw new IOException("Invalid recipient string: " + error);
            }
            for (String recipient : recipientsList) {
                readWriteLine("RCPT TO:<" + recipient.trim() + ">");
            }
            writeLine("DATA");
            String response = in.readLine();
            if (!response.startsWith("354")) {
                throw new IOException("Unexpected response to DATA command: " + response);
            }
            writeLine("From: " + fromAddress);
            writeLine("To: " + recipientString);
            writeLine("Subject: " + mailSubject);
            writeLine("MIME-Version: 1.0");
            writeLine("Content-Type: multipart/mixed; boundary=\"" + boundary + "\"");
            writeLine("");
            writeLine("--" + boundary);
            writeLine("Content-Type: text/plain");
            writeLine("");
            writeLine(mailBody);
            for (AbstractFile file : fileSet.getAttachments()) {
                writeLine("--" + boundary);
                writeLine("Content-Type: application/octet-stream; name=\"" + file.getName() + "\"");
                writeLine("Content-Transfer-Encoding: base64");
                writeLine("");
                sendAttachment(file);
            }
            writeLine("--" + boundary + "--");
            writeLine(".");
            response = in.readLine();
            if (!response.startsWith("250")) {
                throw new IOException("DATA command failed: " + response);
            }
        } catch (IOException e) {
            handleError("Failed to send email body: " + e.getMessage());
        }
    }

    public void sendAttachment(AbstractFile file) throws IOException {
        try (InputStream fileIn = file.getInputStream()) {
            byte[] buffer = new byte[4096];
            int bytesRead;
            while ((bytesRead = fileIn.read(buffer)) != -1) {
                out64.write(buffer, 0, bytesRead);
            }
            out64.flush();
        }
    }

    public void closeConnection() {
        try {
            if (connectedToMailServer) {
                readWriteLine("QUIT");
                in.close();
                out.close();
                socket.close();
                connectedToMailServer = false;
            }
        } catch (IOException e) {
            handleError("Failed to close connection: " + e.getMessage());
        }
    }

    public void readWriteLine(String line) throws IOException {
        writeLine(line);
        String response;
        do {
            response = in.readLine();
        } while (response != null && response.startsWith("354"));
        if (response == null || !response.startsWith("2")) {
            throw new IOException("SMTP error: " + response);
        }
    }

    public void writeLine(String line) throws IOException {
        out.write((line + "\r\n").getBytes());
        out.flush();
    }

    public String splitRecipientString(String input, List<String> recipients) {
        if (input == null || input.isEmpty()) {
            return "Recipient string is empty";
        }
        String[] parts = input.split(",");
        for (String part : parts) {
            recipients.add(part.trim());
        }
        return "";
    }

    private void handleError(String message) {
        JOptionPane.showMessageDialog(null, message, errorDialogTitle, JOptionPane.ERROR_MESSAGE);
        closeConnection();
    }
}
