    private static class BinaryPacket {
        private ByteArrayOutputStream buffer = new ByteArrayOutputStream();
        private int sequence = 0;

        public void writeInt(int value) {
            buffer.write(value >> 24 & 0xFF);
            buffer.write(value >> 16 & 0xFF);
            buffer.write(value >> 8 & 0xFF);
            buffer.write(value & 0xFF);
        }

        public void writeShort(int value) {
            buffer.write(value >> 8 & 0xFF);
            buffer.write(value & 0xFF);
        }

        public void writeByte(byte value) {
            buffer.write(value);
        }

        public void writeString(String value) {
            try {
                buffer.write(value.getBytes("UTF-8"));
                buffer.write(0);
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        }

        public void writeBytes(byte[] value) {
            buffer.write(value);
        }

        public byte[] toByteArray() {
            byte[] data = buffer.toByteArray();
            byte[] packet = new byte[data.length + 4];
            packet[0] = (byte)(data.length & 0xFF);
            packet[1] = (byte)(data.length >> 8 & 0xFF);
            packet[2] = (byte)(data.length >> 16 & 0xFF);
            packet[3] = (byte)sequence++;
            System.arraycopy(data, 0, packet, 4, data.length);
            return packet;
