                out.write(header);
                out.write(data);
                out.flush();
            } catch (IOException e) {
                closeAll();
            }
        });
    }

    public void onReceive(ReceiveListener listener) {
        receiveListener = listener;
    }

    public void destroy() {
        handler.post(() -> {
            closeAll();
            receiveThread.quit();
        });
    }

    private void closeAll() {
