        this.host = host;
        this.port = port;
    }

    public Socket connect() throws IOException {
        InetAddress[] addresses = InetAddress.getAllByName(host);
        for (InetAddress address : addresses) {
            try {
                return connectToAddress(address);
            } catch (IOException e) {
                // Continue to next address
            }
        }
        throw new IOException("Could not connect to any address for host " + host);
    }

    private Socket connectToAddress(InetAddress address) throws IOException {
        Socket socket = new Socket();
        socket.connect(new InetSocketAddress(address, port));
        return socket;
    }

    public void open() throws MessagingException {
        try {
            socket = connect();
            in = new BufferedInputStream(socket.getInputStream());
            out = new BufferedOutputStream(socket.getOutputStream());
        } catch (IOException e) {
            throw new MessagingException("Failed to open connection", e);
        }
    }

    public boolean isOpen() {
        return socket != null && socket.isConnected() && !socket.isClosed();
    }

    public void close() {
        try {
            if (in != null) {
                in.close();
            }
        } catch (IOException e) {
            // Ignore
        }
        try {
            if (out != null) {
                out.close();
            }
        } catch (IOException e) {
            // Ignore
        }
        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
            // Ignore
        }
    }

    public void writeLine(String s) throws IOException {
        out.write(s.getBytes(java.nio.charset.StandardCharsets.ISO_8859_1));
        out.write('\r');
        out.write('\n');
        out.flush();
    }

    public String readLine() throws IOException {
        ByteArrayOutputStream buffer = new ByteArrayOutputStream();
        boolean crReceived = false;
        int b;
        while ((b = in.read()) != -1) {
            if (crReceived) {
                if (b == '\n') {
                    return buffer.toString(java.nio.charset.StandardCharsets.ISO_8859_1.name());
                } else {
                    buffer.write((byte) '\r');
                    buffer.write(b);
                    crReceived = false;
                }
            } else if (b == '\r') {
                crReceived = true;
            } else if (b == '\n') {
                return buffer.toString(java.nio.charset.StandardCharsets.ISO_8859_1.name());
            } else {
                buffer.write(b);
            }
        }
        return buffer.toString(java.nio.charset.StandardCharsets.ISO_8859_1.name());
    }
