            }
            scrcpyLocalThread.interrupt();
            if (androidTestTaskBootThread != null) androidTestTaskBootThread.interrupt();
        }
    }

    private void processNalUnits(ByteArrayOutputStream buffer) {
        byte[] data = buffer.toByteArray();
        int i = 0;
        int lastStart = -1;
        while (i < data.length - 3) {
            if (data[i] == 0x00 && data[i+1] == 0x00 && data[i+2] == 0x00 && data[i+3] == 0x01) {
                if (lastStart != -1) {
