import java.io.*;
import java.net.*;
import java.util.Map;
import java.util.HashMap;
import java.util.logging.Logger;

public class NetworkForwarder {
    private static final Logger LOGGER = Logger.getLogger(NetworkForwarder.class.getName());
    private final InetAddress destination;
    private final DatagramSocket connectionUdp;
    private final Map<InetSocketAddress, Socket> connectionsTcp = new HashMap<>();

    public NetworkForwarder(Config config) throws UnknownHostException, SocketException {
        this.destination = config.getDestinationAddress();
        this.connectionUdp = new DatagramSocket(config.getUdpPort());
    }

    public void forward(InetSocketAddress source, int destinationPort, boolean datagram, byte[] data) {
        if (datagram) {
            try {
                DatagramPacket packet = new DatagramPacket(data, data.length, destination, destinationPort);
                connectionUdp.send(packet);
            } catch (IOException e) {
                LOGGER.warning("Failed to send UDP packet: " + e.getMessage());
            }
        } else {
            Socket socket = connectionsTcp.get(source);
            if (socket == null) {
                try {
                    socket = new Socket();
                    socket.connect(new InetSocketAddress(destination, destinationPort));
                    connectionsTcp.put(source, socket);
                } catch (IOException e) {
                    LOGGER.severe("Failed to create TCP connection: " + e.getMessage());
                    return;
                }
            }
            try {
                OutputStream out = socket.getOutputStream();
                out.write(data);
                out.flush();
            } catch (IOException e) {
                LOGGER.warning("Failed to send TCP data: " + e.getMessage());
                disconnect(source);
            }
        }
    }

    public void disconnect(InetSocketAddress source) {
        Socket socket = connectionsTcp.remove(source);
        if (socket != null) {
            try {
                socket.close();
            } catch (IOException e) {
                LOGGER.warning("Failed to close TCP connection: " + e.getMessage());
            }
        }
    }
}

