            String formattedAttributes = formatAttributes(attributes);
            byte[] data = formattedAttributes.getBytes(StandardCharsets.UTF_8);
            ByteBuf compressedData = compressData(data);

            int deviceId = Integer.parseInt(uniqueId);
            byte[] deviceIdBytes = new byte[4];
            deviceIdBytes[0] = (byte) (deviceId >> 24);
            deviceIdBytes[1] = (byte) (deviceId >> 16);
            deviceIdBytes[2] = (byte) (deviceId >> 8);
            deviceIdBytes[3] = (byte) deviceId;

            byte messageType = 0x02;
            int compressedLength = compressedData.readableBytes();
            byte[] compressedArray = new byte[compressedLength];
            compressedData.readBytes(compressedArray);

            ByteArrayOutputStream bos = new ByteArrayOutputStream();
            bos.write(deviceIdBytes);
            bos.write(messageType);
            bos.write((byte) (compressedLength >> 8));
            bos.write((byte) (compressedLength & 0xFF));
            bos.write(compressedArray);

            byte[] message = bos.toByteArray();
            DatagramPacket packet = new DatagramPacket(message, message.length, address, port);
            socket.send(packet);

            resultHandler.onResult(true, null);
