        Deflater deflater = new Deflater(Deflater.DEFAULT_COMPRESSION, true);
        deflater.setInput(data);
        deflater.finish();
        byte[] buffer = new byte[1024];
        try {
            while (!deflater.finished()) {
                int count = deflater.deflate(buffer);
                if (count > 0) {
                    out.writeBytes(buffer, 0, count);
                }
            }
        } finally {
            deflater.end();
        }
        return out;
    }

