import java.net.*;
import java.util.*;
import java.util.logging.Logger;

public class UdpTransport {
    private DatagramSocket sock;
    private Thread receiverThread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private boolean server;
    private Logger log = Logger.getLogger(UdpTransport.class.getName());
    private List<SocketAddress> members = new ArrayList<>();

    public interface RtReceiver {
        void onPacketReceived(DatagramPacket packet);
    }

    public UdpTransport(boolean isServer) {
        this.server = isServer;
    }

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public Object localAddress() {
        return sock.getLocalSocketAddress();
    }

    public List<? extends Object> clusterMembers() {
        return members;
    }

    public void start(String... options) throws SocketException, UnknownHostException, IOException {
        for (int i = 0; i < options.length; i++) {
            String option = options[i];
            if (option.startsWith("-port=")) {
                this.port = Integer.parseInt(option.substring(6));
            } else if (!server && i == 0) {
                String[] parts = option.split(":");
                if (parts.length != 2) {
                    throw new IllegalArgumentException("Invalid server address format: " + option);
                }
                this.host = InetAddress.getByName(parts[0]);
                this.port = Integer.parseInt(parts[1]);
            } else {
                String[] parts = option.split(":");
                if (parts.length != 2) {
                    throw new IllegalArgumentException("Invalid member address format: " + option);
                }
                String hostStr = parts[0];
                int memberPort = Integer.parseInt(parts[1]);
                InetAddress memberHost = InetAddress.getByName(hostStr);
                members.add(new InetSocketAddress(memberHost, memberPort));
            }
        }

        if (server) {
            sock = new DatagramSocket(port);
            this.host = InetAddress.getLocalHost();
        } else {
            sock = new DatagramSocket();
        }

        receiverThread = new Receiver();
        receiverThread.start();
    }

    public void stop() {
        if (sock != null) {
            sock.close();
        }
        if (receiverThread != null) {
            receiverThread.interrupt();
        }
    }

    public void send(Object destination, byte[] data, int offset, int length) throws IOException {
        SocketAddress destAddr = (SocketAddress) destination;
        DatagramPacket packet = new DatagramPacket(data, offset, length, destAddr);
        sock.send(packet);
    }

    private class Receiver extends Thread {
        @Override
        public void run() {
            byte[] buffer = new byte[65535];
            while (!isInterrupted()) {
                DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
                try {
                    sock.receive(packet);
                    if (receiver != null) {
                        receiver.onPacketReceived(packet);
                    }
                } catch (IOException e) {
                    log.severe("Error receiving packet: " + e.getMessage());
                }
            }
        }
    }
}
