import java.io.IOException;

public class SntpImpl implements Sntp {

    private static final int NTP_PORT = 123;
    private static final int NTP_MODE = 3;
    private static final int NTP_VERSION = 3;
    private static final int NTP_PACKET_SIZE = 48;
    private static final long OFFSET_1900_TO_1970 = 2208988800L;

    public SntpResult requestTime(InetAddress address, float maxOffset, float maxDelay, int timeout, int retries, SntpEventListener listener) {
        DatagramSocket socket = null;
        try {
            socket = new DatagramSocket();
            socket.setSoTimeout(timeout);

            byte[] buffer = new byte[NTP_PACKET_SIZE];
            writeNtpVersion(buffer);

            long currentMillis = System.currentTimeMillis();
            long secondsSince1970 = currentMillis / 1000;
            long secondsSince1900 = secondsSince1970 + OFFSET_1900_TO_1970;
            long fractionMillis = currentMillis % 1000;
            long fraction = (fractionMillis * 0x100000000L) / 1000;
            long originateNtpTime = ((secondsSince1900 << 32) & 0xFFFFFFFFFFFFFL) | (fraction & 0xFFFFFFFFL);
            writeTimeStamp(buffer, 16, originateNtpTime);

            DatagramPacket request = new DatagramPacket(buffer, buffer.length, address, NTP_PORT);
            socket.send(request);

            DatagramPacket response = new DatagramPacket(buffer, buffer.length);
            socket.receive(response);

            int mode = buffer[0] & 0x7;
            if (mode != 4) {
                throw new IOException("Invalid NTP mode: " + mode);
            }

            int vn = (buffer[0] >> 3) & 0x7;
            if (vn != NTP_VERSION) {
                throw new IOException("Invalid NTP version: " + vn);
            }

            int stratum = buffer[1] & 0xFF;
            if (stratum == 0) {
                throw new IOException("Invalid stratum: 0");
            }

            long t1 = originateNtpTime;
            long t2 = readTimeStamp(buffer, 24);
            long t3 = readTimeStamp(buffer, 32);

            long currentMillisT4 = System.currentTimeMillis();
            long secondsSince1970T4 = currentMillisT4 / 1000;
            long secondsSince1900T4 = secondsSince1970T4 + OFFSET_1900_TO_1970;
            long fractionMillisT4 = currentMillisT4 % 1000;
            long fractionT4 = (fractionMillisT4 * 0x100000000L) / 1000;
            long t4 = ((secondsSince1900T4 << 32) & 0xFFFFFFFFFFFFFL) | (fractionT4 & 0xFFFFFFFFL);

            long offsetNtp = (t2 - t1) + (t3 - t4);
            double offsetSeconds = (double) offsetNtp / (1L << 32);
            double offsetMillis = offsetSeconds * 1000.0;

            long delayNtp = (t4 - t1) - (t3 - t2);
            double delaySeconds = (double) delayNtp / (1L << 32);
            double delayMillis = delaySeconds * 1000.0;

            if (Math.abs(offsetMillis) > maxOffset * 1000.0 || delayMillis > maxDelay * 1000.0) {
                listener.onSntpError(new IOException("Thresholds exceeded"));
            } else {
                listener.onSntpSuccess(new SntpResult(offsetMillis, delayMillis));
            }

            return new SntpResult(offsetMillis, delayMillis);
        } catch (Exception e) {
            listener.onSntpError(e);
            return null;
        } finally {
            if (socket != null) {
                socket.close();
            }
        }
    }

    private void writeNtpVersion(byte[] buffer) {
        buffer[0] = (byte) 0x1B;
    }

    private void writeTimeStamp(byte[] buffer, int offset, long ntpTime) {
        long seconds = (ntpTime >>> 32) & 0xFFFFFFFFL;
        long fraction = ntpTime & 0xFFFFFFFFL;
        buffer[offset] = (byte) ((seconds >> 24) & 0xFF);
        buffer[offset + 1] = (byte) ((seconds >> 16) & 0xFF);
        buffer[offset + 2] = (byte) ((seconds >> 8) & 0xFF);
        buffer[offset + 3] = (byte) (seconds & 0xFF);
        buffer[offset + 4] = (byte) ((fraction >> 24) & 0xFF);
        buffer[offset + 5] = (byte) ((fraction >> 16) & 0xFF);
        buffer[offset + 6] = (byte) ((fraction >> 8) & 0xFF);
        buffer[offset + 7] = (byte) (fraction & 0xFF);
    }

    private long readTimeStamp(byte[] buffer, int offset) {
        long seconds = read(buffer, offset);
        long fraction = read(buffer, offset + 4);
        return (seconds << 32) | (fraction & 0xFFFFFFFFL);
    }

    private long read(byte[] buffer, int offset) {
        return ((buffer[offset] & 0xFFL) << 24) |
               ((buffer[offset + 1] & 0xFFL) << 16) |
               ((buffer[offset + 2] & 0xFFL) << 8) |
               (buffer[offset + 3] & 0xFFL);
    }

    private int ui(byte b) {
        return b & 0xFF;
    }

    private double doubleMillis(long ntpTime) {
        long seconds = (ntpTime >>> 32) & 0xFFFFFFFFL;
        long fraction = ntpTime & 0xFFFFFFFFL;
        double timeInSeconds = seconds + (fraction / (double) (1L << 32));
        return timeInSeconds * 1000.0;
    }

    public static class SntpResult {
        public final double offset;
        public final double delay;

        public SntpResult(double offset, double delay) {
            this.offset = offset;
            this.delay = delay;
        }
    }

    public interface SntpEventListener {
        void onSntpSuccess(SntpResult result);
        void onSntpError(Exception e);
    }
}
