import java.net.*;
import java.util.*;
import android.content.Context;
import android.content.SharedPreferences;

public class LanLinkProvider {
    private Context context;
    private DatagramSocket udpServer;
    private volatile boolean listening;
    private static final int UDP_PORT = 1716;
    private static final int MIN_PORT = 1716;
    private static final int MAX_UDP_PACKET_SIZE = 512 * 1024;

    public LanLinkProvider(Context context) {
        this.context = context;
    }

    public void setupUdpListener() {
        new Thread(() -> {
            try {
                udpServer = new DatagramSocket(UDP_PORT);
                udpServer.setBroadcast(true);
                configureSocket(udpServer);
                listening = true;
                byte[] buffer = new byte[MAX_UDP_PACKET_SIZE];
                DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
                while (listening) {
                    try {
                        udpServer.receive(packet);
                        udpPacketReceived(packet);
                    } catch (SocketTimeoutException e) {
                        if (!listening) break;
                    }
                }
            } catch (Exception e) {
                e.printStackTrace();
            } finally {
                if (udpServer != null) {
                    udpServer.close();
                }
            }
        }).start();
    }

    private void configureSocket(Socket socket) {
        try {
            socket.setReuseAddress(true);
            socket.setSoTimeout(1000);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void udpPacketReceived(DatagramPacket packet) {
        try {
            String data = new String(packet.getData(), 0, packet.getLength());
            String deviceId = extractDeviceId(data);
            if (deviceId != null && !isDeviceTrusted(deviceId)) {
                SharedPreferences.Editor editor = context.getSharedPreferences("lan_trust", Context.MODE_PRIVATE).edit();
                editor.putBoolean(deviceId, true);
                editor.apply();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private String extractDeviceId(String data) {
        try {
            int start = data.indexOf("\"deviceId\":\"") + 11;
            int end = data.indexOf("\"", start);
            return data.substring(start, end);
        } catch (Exception e) {
            return null;
        }
    }

    public void sendUdpIdentityPacket(List<InetAddress> ipList, Network network) {
        try {
            String deviceId = Settings.Secure.getString(context.getContentResolver(), Settings.Secure.ANDROID_ID);
            String deviceName = android.os.Build.MODEL;
            String payload = String.format("{\"deviceId\":\"%s\",\"name\":\"%s\"}", deviceId, deviceName);
            byte[] buffer = payload.getBytes();

            DatagramPacket packet = new DatagramPacket(
                buffer, 
                buffer.length, 
                InetAddress.getByName("255.255.255.255"), 
                UDP_PORT
            );

            if (network != null) {
                try {
                    Method setNetworkMethod = DatagramSocket.class.getMethod("setNetwork", Network.class);
                    setNetworkMethod.invoke(udpServer, network);
                } catch (Exception ignored) {}
            }

            for (InetAddress address : ipList) {
                packet.setAddress(address);
                udpServer.send(packet);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public void onStart() {
        setupUdpListener();
    }

    public void onStop() {
        listening = false;
        if (udpServer != null) {
            udpServer.close();
        }
    }

    private boolean isDeviceTrusted(String deviceId) {
        SharedPreferences prefs = context.getSharedPreferences("lan_trust", Context.MODE_PRIVATE);
        return prefs.getBoolean(deviceId, false);
    }
}

