import java.net.*;
import java.nio.ByteBuffer;
import java.util.*;
import java.util.logging.Logger;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;

public class UDPTrackerClient {
    private final Logger logger = Logger.getLogger(UDPTrackerClient.class.getName());
    private static final int UDP_BASE_TIMEOUT_SECONDS = 15;
    private static final int UDP_MAX_TRIES = 3;
    private static final int UDP_MAX_TRIES_ON_STOPPED = 1;
    private static final int UDP_PACKET_LENGTH = 65535;
    private final InetSocketAddress address;
    private final Random random = new Random();
    private final List<Peer> peers;
    private DatagramSocket socket;
    private Date connectionExpiration;
    private long connectionId;
    private int transactionId;
    private boolean stop;

    public UDPTrackerClient(List<Peer> peers, URI tracker) throws URISyntaxException, SocketException {
        this.peers = peers;
        this.address = new InetSocketAddress(tracker.getHost(), tracker.getPort());
        this.socket = new DatagramSocket();
        this.connectionId = 0x41727101980LL; // Initial connection ID
        this.transactionId = random.nextInt();
    }

    public void announce(AnnounceRequestMessage.RequestEvent event, boolean inhibitEvents, AnnounceableInformation torrent, List<Peer> peers) {
        try {
            if (!inhibitEvents && event != AnnounceRequestMessage.RequestEvent.STOPPED) {
                UDPAnnounceRequestMessage request = buildAnnounceRequest(event, torrent, peers.get(0));
                send(request.toByteBuffer());
                ByteBuffer response = recv(0);
                handleTrackerAnnounceResponse(new TrackerMessage(response), inhibitEvents, torrent.getInfoHashHex());
            }
        } catch (Exception e) {
            logger.severe("Announce failed: " + e.getMessage());
        }
    }

    private void handleTrackerAnnounceResponse(TrackerMessage message, boolean inhibitEvents, String hexInfoHash) {
        validateTrackerResponse(message);
        if (message.getAction() == TrackerMessage.Action.ANNOUNCE) {
            // Process peers list
            byte[] peersData = message.getPeers();
            for (int i = 0; i < peersData.length; i += 6) {
                InetAddress addr = InetAddress.getByAddress(Arrays.copyOfRange(peersData, i, i + 4));
                int port = ((peersData[i + 4] & 0xFF) << 8) | (peersData[i + 5] & 0xFF);
                peers.add(new Peer(addr, port, hexInfoHash));
            }
        }
    }

    public void close() {
        if (socket != null && !socket.isClosed()) {
            socket.close();
        }
    }

    private UDPAnnounceRequestMessage buildAnnounceRequest(AnnounceRequestMessage.RequestEvent event, AnnounceableInformation torrent, Peer peer) {
        transactionId = random.nextInt();
        return new UDPAnnounceRequestMessage(
            connectionId,
            TrackerMessage.Action.ANNOUNCE,
            transactionId,
            torrent.getInfoHash(),
            peer.getPeerId(),
            event,
            torrent.getLeft(),
            torrent.getDownloaded(),
            torrent.getUploaded(),
            peer.getAddress().getPort(),
            torrent.getNumWant()
        );
    }

    private void validateTrackerResponse(TrackerMessage message) {
        if (message.getAction() != TrackerMessage.Action.CONNECT && 
            message.getAction() != TrackerMessage.Action.ANNOUNCE) {
            throw new IllegalArgumentException("Invalid action in response");
        }
        if (message.getTransactionId() != transactionId) {
            throw new IllegalArgumentException("Transaction ID mismatch");
        }
    }

    private void handleTrackerConnectResponse(TrackerMessage message) {
        if (message.getAction() == TrackerMessage.Action.CONNECT) {
            connectionId = message.getConnectionId();
            connectionExpiration = new Date(System.currentTimeMillis() + 2 * 60 * 1000); // 2 minutes
        }
    }

    private void send(ByteBuffer data) throws IOException {
        DatagramPacket packet = new DatagramPacket(data.array(), data.limit(), address);
        socket.send(packet);
    }

    private ByteBuffer recv(int attempt) throws IOException, InterruptedException {
        socket.setSoTimeout(UDP_BASE_TIMEOUT_SECONDS * 1000);
        ByteBuffer buffer = ByteBuffer.allocate(UDP_PACKET_LENGTH);
        DatagramPacket packet = new DatagramPacket(buffer.array(), buffer.capacity());
        try {
            socket.receive(packet);
            buffer.limit(packet.getLength());
            return buffer;
        } catch (SocketTimeoutException e) {
            if (attempt < UDP_MAX_TRIES) {
                return recv(attempt + 1);
            }
            throw e;
        }
    }
}

class TrackerMessage {
    public enum Action { CONNECT, ANNOUNCE }
    private final long connectionId;
    private final Action action;
    private final int transactionId;
    private final byte[] peers;

    public TrackerMessage(ByteBuffer buffer) {
        // Parse buffer according to BitTorrent UDP tracker protocol
        connectionId = buffer.getLong();
        action = Action.values()[(int)(buffer.getLong() & 0xFF)];
        transactionId = buffer.getInt();
        // Additional parsing based on action type
        peers = new byte[buffer.remaining()];
        buffer.get(peers);
    }

    // Getters
    public long getConnectionId() { return connectionId; }
    public Action getAction() { return action; }
    public int getTransactionId() { return transactionId; }
    public byte[] getPeers() { return peers; }
}

class UDPAnnounceRequestMessage {
    private final long connectionId;
    private final TrackerMessage.Action action;
    private final int transactionId;
    private final byte[] infoHash;
    private final byte[] peerId;
    private final AnnounceRequestMessage.RequestEvent event;
    private final long left;
    private final long downloaded;
    private final long uploaded;
    private final int port;
    private final int numWant;

    public UDPAnnounceRequestMessage(long connectionId, TrackerMessage.Action action, int transactionId, byte[] infoHash, byte[] peerId, AnnounceRequestMessage.RequestEvent event, long left, long downloaded, long uploaded, int port, int numWant) {
        this.connectionId = connectionId;
        this.action = action;
        this.transactionId = transactionId;
        this.infoHash = infoHash;
        this.peerId = peerId;
        this.event = event;
        this.left = left;
        this.downloaded = downloaded;
        this.uploaded = uploaded;
        this.port = port;
        this.numWant = numWant;
    }

    public ByteBuffer toByteBuffer() {
        ByteBuffer buffer = ByteBuffer.allocate(98);
        buffer.putLong(connectionId);
