                if (isClosing) {
                    break;
                }
            } catch (Exception e) {
                if (isClosing) {
                    break;
                }
            } catch (ThreadDeath d) {
                close();
                throw d;
            } catch (Error err) {
                handleJavaError(err);
            }
        }
    }

    @Override
    protected synchronized void finalize() {
        close();
    }

    private synchronized void handleJavaError(Throwable thr) {
        if (thr instanceof OutOfMemoryError) {
            Thread.yield();
            return;
        }
        if (_socket != null) {
            _socket.close();
            _socket = null;
        }
