import java.net.*;
import java.util.ArrayList;
import java.util.List;

public class DnsUdpClient {
    public static final InetSocketAddress[] DNS_SERVERS = new InetSocketAddress[]{
        new InetSocketAddress("8.8.8.8", 53),
        new InetSocketAddress("1.1.1.1", 53)
    };

    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        try {
            socket.setSoTimeout(5000);
            DatagramPacket sendPacket = new DatagramPacket(query, query.length, dnsServer);
            socket.send(sendPacket);

            byte[] responseBuffer = new byte[512];
            DatagramPacket receivePacket = new DatagramPacket(responseBuffer, responseBuffer.length);
            socket.receive(receivePacket);

            return toIps(receivePacket.getData());
        } finally {
            socket.close();
        }
    }

    private List<Inet4Address> toIps(byte[] buffer) {
        List<Inet4Address> ips = new ArrayList<>();
        if (buffer == null || buffer.length < 12) {
            return ips;
        }

        int offset = 0;

        int answerCount = ((buffer[6] & 0xFF) << 8) | (buffer[7] & 0xFF);
        int rcode = buffer[3] & 0x0F;
        if (rcode != 0) {
            return ips;
        }

        offset = 12;
        while (offset < buffer.length && buffer[offset] != 0) {
            int len = buffer[offset] & 0xFF;
            if ((len & 0xC0) == 0xC0) {
                offset += 2;
                break;
            }
            offset += len + 1;
        }
        offset += 1;
        offset += 4;

        for (int i = 0; i < answerCount; i++) {
            while (offset < buffer.length) {
                int len = buffer[offset] & 0xFF;
                if (len == 0) {
                    offset += 1;
                    break;
                }
                if ((len & 0xC0) == 0xC0) {
                    offset += 2;
                    break;
                }
                offset += len + 1;
            }

            int type = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            offset += 2;
            int clazz = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            offset += 2;

            offset += 4;

            int rdLength = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            offset += 2;

            if (type == 1 && clazz == 1) {
                if (rdLength == 4 && offset + 4 <= buffer.length) {
                    byte[] ipBytes = new byte[4];
                    System.arraycopy(buffer, offset, ipBytes, 0, 4);
                    try {
                        ips.add((Inet4Address) Inet4Address.getByAddress(ipBytes));
                    } catch (UnknownHostException e) {
                    }
                }
                offset += rdLength;
            } else {
                offset += rdLength;
            }
        }

        return ips;
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        try {
            socket.setSoTimeout(5000);
            DatagramPacket sendPacket = new DatagramPacket(query, query.length, dnsServer);
            socket.send(sendPacket);

            byte[] responseBuffer = new byte[512];
            DatagramPacket receivePacket = new DatagramPacket(responseBuffer, responseBuffer.length);
            socket.receive(receivePacket);

            return toTXT(receivePacket.getData());
        } finally {
            socket.close();
        }
    }

    private String toTXT(byte[] buffer) {
        if (buffer == null || buffer.length < 12) {
            throw new RuntimeException("Invalid DNS response");
        }

        int offset = 0;

        int answerCount = ((buffer[6] & 0xFF) << 8) | (buffer[7] & 0xFF);
        int rcode = buffer[3] & 0x0F;
        if (rcode != 0) {
            throw new RuntimeException("DNS query failed with RCODE " + rcode);
        }

        offset = 12;
        while (offset < buffer.length && buffer[offset] != 0) {
            int len = buffer[offset] & 0xFF;
            if ((len & 0xC0) == 0xC0) {
                offset += 2;
                break;
            }
            offset += len + 1;
        }
        offset += 1;
        offset += 4;

        for (int i = 0; i < answerCount; i++) {
            while (offset < buffer.length) {
                int len = buffer[offset] & 0xFF;
                if (len == 0) {
                    offset += 1;
                    break;
                }
                if ((len & 0xC0) == 0xC0) {
                    offset += 2;
                    break;
                }
                offset += len + 1;
            }

            int type = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            offset += 2;
            int clazz = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            offset += 2;

            offset += 4;

            int rdLength = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            offset += 2;

            if (type == 16 && clazz == 1) {
                StringBuilder txt = new StringBuilder();
                int rdOffset = offset;
                int remaining = rdLength;
                while (remaining > 0) {
                    int strLen = buffer[rdOffset] & 0xFF;
                    if (strLen == 0) break;
                    if (rdOffset + 1 + strLen > offset + rdLength) break;
                    txt.append(new String(buffer, rdOffset + 1, strLen));
                    txt.append(' ');
                    rdOffset += 1 + strLen;
                    remaining -= 1 + strLen;
                }
                if (txt.length() > 0) {
                    txt.setLength(txt.length() - 1);
                    return txt.toString();
                } else {
                    return "";
                }
            } else {
                offset += rdLength;
            }
        }

        throw new RuntimeException("No TXT record found in response");
    }
}
