import java.io.*;
import java.net.*;
import java.nio.charset.StandardCharsets;
import java.util.*;

public final class HttpURLConnectionUtils {
    private static final int TIMEOUT_IN_MILLIONS = 5000;

    public interface Callback {
        void onResponse(String result, long responseCode);
        void onFail(Throwable error);
    }

    public static void doGetAsync(String urlStr, Callback callback) {
        request("GET", urlStr, new HashMap<>(), "", callback);
    }

    public static void doPostAsync(String urlStr, String params, Callback callback) {
        request("POST", urlStr, new HashMap<>(), params, callback);
    }

    private static void request(String method, String urlStr, Map<String, String> headers, String params, Callback callback) {
        new Thread(() -> {
            HttpURLConnection conn = null;
            try {
                URL url = new URL(urlStr);
                conn = (HttpURLConnection) url.openConnection();
                conn.setRequestMethod(method);
                conn.setConnectTimeout(TIMEOUT_IN_MILLIONS);
                conn.setReadTimeout(TIMEOUT_IN_MILLIONS);
                conn.setDoOutput("POST".equals(method));

                if (headers != null) {
                    for (Map.Entry<String, String> entry : headers.entrySet()) {
                        conn.setRequestProperty(entry.getKey(), entry.getValue());
                    }
                }

                if ("POST".equals(method) && params != null && !params.isEmpty()) {
                    try (OutputStream os = conn.getOutputStream()) {
                        os.write(params.getBytes(StandardCharsets.UTF_8));
                    }
                }

                int responseCode = conn.getResponseCode();
                String responseBody;

                InputStream inputStream;
                if (responseCode >= 300) {
                    inputStream = conn.getErrorStream();
                } else {
                    inputStream = conn.getInputStream();
                }

                if (inputStream != null) {
                    try (BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream))) {
                        StringBuilder sb = new StringBuilder();
                        String line;
                        while ((line = reader.readLine()) != null) {
                            sb.append(line);
                        }
                        responseBody = sb.toString();
                    }
                } else {
                    responseBody = "";
                }

                callback.onResponse(responseBody, responseCode);
            } catch (Exception e) {
                callback.onFail(e);
            } finally {
                if (conn != null) {
                    conn.disconnect();
                }
            }
        }).start();
    }

    private HttpURLConnectionUtils() {
    }
}
