import java.io.*;
import java.net.*;
import java.nio.charset.Charset;
import java.util.List;

public class HttpTinyClient {
    public static class HttpResult {
        public final int code;
        public final String content;

        public HttpResult(int code, String content) {
            this.code = code;
            this.content = content;
        }
    }

    private static String encodingParams(List<String> paramValues, String encoding) throws UnsupportedEncodingException {
        StringBuilder sb = new StringBuilder();
        for (String param : paramValues) {
            int idx = param.indexOf('=');
            if (idx == -1) {
                String key = param;
                String encodedKey = URLEncoder.encode(key, encoding);
                if (sb.length() > 0) {
                    sb.append('&');
                }
                sb.append(encodedKey);
            } else {
                String key = param.substring(0, idx);
                String value = param.substring(idx + 1);
                String encodedKey = URLEncoder.encode(key, encoding);
                String encodedValue = URLEncoder.encode(value, encoding);
                if (sb.length() > 0) {
                    sb.append('&');
                }
                sb.append(encodedKey).append('=').append(encodedValue);
            }
        }
        return sb.toString();
    }

    private static void setHeaders(HttpURLConnection conn, List<String> headers, String encoding) {
        boolean contentTypeSet = false;
        boolean userAgentSet = false;

        for (String header : headers) {
            int colonIndex = header.indexOf(':');
            if (colonIndex == -1) continue;
            String name = header.substring(0, colonIndex).trim();
            String value = header.substring(colonIndex + 1).trim();
            conn.setRequestProperty(name, value);
            if (name.equalsIgnoreCase("Content-Type")) {
                contentTypeSet = true;
            } else if (name.equalsIgnoreCase("User-Agent")) {
                userAgentSet = true;
            }
        }

        if (!userAgentSet) {
            conn.setRequestProperty("User-Agent", "HttpTinyClient/1.0");
        }

        String requestMethod = conn.getRequestMethod();
        if (requestMethod.equals("POST") && !contentTypeSet) {
            conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded; charset=" + encoding);
        }
    }

    private static String readResponse(HttpURLConnection conn, int responseCode, String encoding) throws IOException {
        InputStream inputStream;
        if (responseCode >= 400) {
            inputStream = conn.getErrorStream();
        } else {
            inputStream = conn.getInputStream();
        }
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream, encoding))) {
            StringBuilder sb = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null) {
                sb.append(line);
            }
            return sb.toString();
        }
    }

    public static HttpResult httpGet(String url, List<String> headers, List<String> paramValues, String encoding, long readTimeoutMs) throws IOException {
        String encodedParams;
        try {
            encodedParams = encodingParams(paramValues, encoding);
        } catch (UnsupportedEncodingException e) {
            throw new IOException("Encoding failed", e);
        }

        String fullUrl = url;
        if (!encodedParams.isEmpty()) {
            if (fullUrl.contains("?")) {
                fullUrl += "&" + encodedParams;
            } else {
                fullUrl += "?" + encodedParams;
            }
        }

        URL obj = new URL(fullUrl);
        HttpURLConnection conn = (HttpURLConnection) obj.openConnection();
        conn.setRequestMethod("GET");
        conn.setReadTimeout((int) readTimeoutMs);
        setHeaders(conn, headers, encoding);

        int code;
        try {
            code = conn.getResponseCode();
        } catch (IOException e) {
            conn.disconnect();
            throw e;
        }

        String content;
        try {
            content = readResponse(conn, code, encoding);
        } catch (IOException e) {
            conn.disconnect();
            throw e;
        } finally {
            conn.disconnect();
        }

        return new HttpResult(code, content);
    }

    public static HttpResult httpPost(String url, List<String> headers, List<String> paramValues, String encoding, long readTimeoutMs) throws IOException {
        String encodedParams;
        try {
            encodedParams = encodingParams(paramValues, encoding);
        } catch (UnsupportedEncodingException e) {
            throw new IOException("Encoding failed", e);
        }

        URL obj = new URL(url);
        HttpURLConnection conn = (HttpURLConnection) obj.openConnection();
        conn.setRequestMethod("POST");
        conn.setReadTimeout((int) readTimeoutMs);
        setHeaders(conn, headers, encoding);
        conn.setDoOutput(true);

        try {
            Charset charset = Charset.forName(encoding);
            byte[] input = encodedParams.getBytes(charset);
            try (OutputStream os = conn.getOutputStream()) {
                os.write(input);
            }
        } catch (IOException e) {
            conn.disconnect();
            throw e;
        }

        int code;
        String content;
        try {
            code = conn.getResponseCode();
            content = readResponse(conn, code, encoding);
        } catch (IOException e) {
            conn.disconnect();
            throw e;
        } finally {
            conn.disconnect();
        }

        return new HttpResult(code, content);
    }
}
