import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;
import java.nio.charset.StandardCharsets;
import java.util.Map;
import java.util.logging.Level;
import java.util.logging.Logger;

public class HttpPostUploadUtil {

    private static final Logger logger = Logger.getLogger(HttpPostUploadUtil.class.getName());

    public static String formUpload(String urlStr, int connectTimeout, int readTimeout, Map<String, String> textMap, Map<String, MultipartFile> fileMap) {
        String boundary = "===" + System.currentTimeMillis() + "===";
        HttpURLConnection connection = null;
        OutputStream outputStream = null;
        InputStream inputStream = null;
        try {
            URL url = new URL(urlStr);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("POST");
            connection.setConnectTimeout(connectTimeout);
            connection.setReadTimeout(readTimeout);
            connection.setDoOutput(true);
            connection.setRequestProperty("Content-Type", "multipart/form-data; boundary=" + boundary);

            outputStream = connection.getOutputStream();
            BufferedOutputStream bufferedOutputStream = new BufferedOutputStream(outputStream);

            if (textMap != null) {
                for (Map.Entry<String, String> entry : textMap.entrySet()) {
                    String name = entry.getKey();
                    String value = entry.getValue();
                    bufferedOutputStream.write(("--" + boundary + "\r\n").getBytes(StandardCharsets.UTF_8));
                    bufferedOutputStream.write(("Content-Disposition: form-data; name=\"" + name + "\"\r\n\r\n").getBytes(StandardCharsets.UTF_8));
                    bufferedOutputStream.write((value + "\r\n").getBytes(StandardCharsets.UTF_8));
                }
            }

            if (fileMap != null) {
                for (Map.Entry<String, MultipartFile> entry : fileMap.entrySet()) {
                    String name = entry.getKey();
                    MultipartFile file = entry.getValue();
                    String fileName = file.getOriginalFilename();
                    String contentType = URLConnection.guessContentTypeFromName(fileName);
                    if (contentType == null) {
                        contentType = "application/octet-stream";
                    }
                    bufferedOutputStream.write(("--" + boundary + "\r\n").getBytes(StandardCharsets.UTF_8));
                    bufferedOutputStream.write(("Content-Disposition: form-data; name=\"" + name + "\"; filename=\"" + fileName + "\"\r\n").getBytes(StandardCharsets.UTF_8));
                    bufferedOutputStream.write(("Content-Type: " + contentType + "\r\n\r\n").getBytes(StandardCharsets.UTF_8));

                    InputStream fileInputStream = file.getInputStream();
                    byte[] buffer = new byte[4096];
                    int bytesRead;
                    while ((bytesRead = fileInputStream.read(buffer)) != -1) {
                        bufferedOutputStream.write(buffer, 0, bytesRead);
                    }
                    fileInputStream.close();
                    bufferedOutputStream.write("\r\n".getBytes(StandardCharsets.UTF_8));
                }
            }

            bufferedOutputStream.write(("--" + boundary + "--\r\n").getBytes(StandardCharsets.UTF_8));
            bufferedOutputStream.flush();
            bufferedOutputStream.close();

            int responseCode = connection.getResponseCode();
            inputStream = (responseCode >= 200 && responseCode < 300) ? connection.getInputStream() : connection.getErrorStream();
            BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream != null ? inputStream : new ByteArrayInputStream(new byte[0]), StandardCharsets.UTF_8));
            StringBuilder response = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null) {
                response.append(line);
            }
            return response.toString();
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Error during HTTP POST upload", e);
            return null;
        } finally {
            closeStream((Closeable) outputStream);
            closeStream((Closeable) inputStream);
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private static void closeStream(Closeable closeable) {
        if (closeable != null) {
            try {
                closeable.close();
            } catch (IOException e) {
                logger.log(Level.SEVERE, "Failed to close stream", e);
            }
        }
    }
}
