                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        public void close() {
            if (channel != null) {
                try {
                    channel.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }

        public record LazyBlock(long offset, int length, FileChannel channel) {
            public Iterable<OsmElement> decodeElements() {
                return List.of();
            }
        }
    }

    private static class BlobHeader {
        private final byte[] data;

        public BlobHeader(byte[] data) {
            this.data = data;
        }

        public long getBlockOffset() {
            return 0;
        }

        public int getBlockLength() {
            return 0;
        }
    }
