public class NettyHttp2Client {

    private final EventLoopGroup workerGroup;
    private final Http2ClientInitializer initializer;
    private final Bootstrap bootstrap;
    private Channel channel;

    public NettyHttp2Client(String host, int port) {
        this.workerGroup = new NioEventLoopGroup();
        this.initializer = new Http2ClientInitializer(Integer.MAX_VALUE);
        this.bootstrap = new Bootstrap();
        configureBootstrap(host, port);
    }

    private void configureBootstrap(String host, int port) {
        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .remoteAddress(host, port)
                .handler(initializer);
    }

    public void connect() {
        channel = bootstrap.connect().syncUninterruptibly().channel();
        Http2SettingsHandler http2SettingsHandler = initializer.settingsHandler();
        http2SettingsHandler.awaitSettings(5, TimeUnit.SECONDS);
