import java.net.http.HttpResponse;

public class WidgetUtil {
    private static final String WIDGET_URL = "https://discord.com/api/guilds/{guildId}/widget";

    public Widget getWidget(long guildId) {
        try {
            String url = WIDGET_URL.replace("{guildId}", Long.toString(guildId));
            HttpRequest request = HttpRequest.newBuilder()
                    .uri(URI.create(url))
                    .GET()
                    .build();

            HttpResponse<String> response = HttpClient.newHttpClient().send(request, HttpResponse.BodyHandlers.ofString());

            int statusCode = response.statusCode();

            if (statusCode == 200) {
                ObjectMapper mapper = new ObjectMapper();
                return mapper.readValue(response.body(), Widget.class);
            } else if (statusCode == 403 || statusCode == 404) {
                return null;
            } else if (statusCode == 429) {
                throw new RateLimitedException("Rate limited");
            } else {
                throw new UncheckedIOException(new IOException("HTTP error " + statusCode));
            }
        } catch (IOException | InterruptedException e) {
            throw new UncheckedIOException(e);
        }
    }
}

