public class DanbooruApiClient {
    private static final String DOMAIN = "danbooru.donmai.us";
    private final OkHttpClient client;
    private Pattern gidPattern = null;
    private int currentPageNum = 1;

    public DanbooruApiClient() {
        this.client = new OkHttpClient.Builder()
                .readTimeout(60, TimeUnit.SECONDS)
                .writeTimeout(60, TimeUnit.SECONDS)
                .build();
    }

    public JSONObject getPage(int pageNum) throws MalformedURLException {
        Request request = new Request.Builder()
                .url("https://" + DOMAIN + "/posts.json?page=" + pageNum)
                .header("User-Agent", "Mozilla/5.0 (iPhone; CPU iPhone OS 15_0 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/15.0 Mobile/15E148 Safari/604.1")
                .header("Accept", "application/json,text/javascript,*/*;q=0.01")
                .header("Accept-Language", "en-US,en;q=0.9")
                .header("Sec-Fetch-Dest", "empty")
                .header("Sec-Fetch-Mode", "cors")
                .header("Sec-Fetch-Site", "same-origin")
                .header("Referer", "https://danbooru.donmai.us/")
                .header("X-Requested-With", "XMLHttpRequest")
                .header("Connection", "keep-alive")
                .build();

        try (Response response = client.newCall(request).execute()) {
            if (!response.isSuccessful()) throw new IOException("Unexpected code " + response);

            String responseData = response.body().string();
            JSONArray jsonArray = new JSONArray(responseData);
            if (!jsonArray.isEmpty()) {
                return new JSONObject("{ \"resources\":" + jsonArray + " }");
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        return null;
    }

    public List<String> extractUrlsFromJson(JSONObject json) {
        List<String> res = new ArrayList<>(100);
        JSONArray jsonArray = json.getJSONArray("resources");
        for (int i = 0; i < jsonArray.length(); i++) {
            if (jsonArray.getJSONObject(i).has("file_url")) {
                res.add(jsonArray.getJSONObject(i).getString("file_url"));
            }
        }
        return res;
    }

    public String getTagFromUrl(URL url) throws MalformedURLException {
        gidPattern = Pattern.compile("https?://danbooru.donmai.us/(posts)?.*([?&]tags=([^&]*)(?:&z=([0-9]+))?$)");
        Matcher m = gidPattern.matcher(url.toExternalForm());

        if (m.matches()) {
            return m.group(3);
        }

        throw new MalformedURLException("Expected danbooru URL format: " + DOMAIN + "/posts?tags=searchterm - got " + url + " instead");
    }
}

