        int retryCount = 0;
        int maxRetry = 5;
        byte[] downloadbBuffer = new byte[1024*1024];
        Response response = null;
        Call call = null;
        boolean directResp = false;
        while (retryCount < maxRetry && !closed) {
            try {
                directResp = false;
                call = downloadClient.newCall(request);
                response = call.execute();
                if (!response.isSuccessful()) {
                    continue;
                }
                if (range.isEmpty()) {
