        long offset = record.offset();
        long timestamp = record.timestamp();
        ValidationStatus status = this.storeUpdateHandler.validateUpdate(
                messageKey, message, tp, offset, timestamp);

        if (status == ValidationStatus.SUCCESS) {
          if (message == null) {
            localStore.delete(messageKey);
          } else {
            localStore.put(messageKey, message);
          }
        }

        try {
          offsetUpdateLock.lock();
          offsetInSchemasTopic = record.offset();
          offsetReachedThreshold.signalAll();
        } finally {
          offsetUpdateLock.unlock();
        }
      }
    } catch (WakeupException we) {
      // Thread is closing
    } catch (RecordTooLargeException rtle) {
      throw new IllegalStateException("Consumer threw RecordTooLargeException", rtle);
    } catch (RuntimeException e) {
      log.error("KafkaStoreReader thread has died", e);
      throw e;
    }
  }

  @Override
  public void shutdown() {
    super.initiateShutdown();
    if (consumer != null) {
      consumer.wakeup();
    }
    super.awaitShutdown();
    if (consumer != null) {
      consumer.close();
    }
  }
}

