public class NioHttpServer {

    private ServerSocketChannel serverSocketChannel;
    private Selector selector;
    private Selector clientReadSelector;
    private Selector clientWriteSelector;

    public NioHttpServer(int port) throws IOException {
        serverSocketChannel = ServerSocketChannel.open();
        selector = Selector.open();
        clientReadSelector = Selector.open();
        clientWriteSelector = Selector.open();

        serverSocketChannel.socket().bind(new InetSocketAddress(port));
        serverSocketChannel.configureBlocking(false);
        serverSocketChannel.register(selector, SelectionKey.OP_ACCEPT);
    }

    public void start() {
        new Thread(new ServerChannelThread(selector, clientReadSelector)).start();
        new Thread(new ClientReadChannelThread(clientReadSelector, clientWriteSelector)).start();
        new Thread(new ClientWriteChannelThread(clientReadSelector, clientWriteSelector)).start();
    }

    private static class ServerChannelThread implements Runnable {
        private Selector serverSelector;
        private Selector clientReadSelector;

        public ServerChannelThread(Selector serverSelector, Selector clientReadSelector) {
            this.serverSelector = serverSelector;
            this.clientReadSelector = clientReadSelector;
        }

        @Override
        public void run() {
            try {
                while (true) {
                    if (serverSelector.select(1) > 0) {
                        Set<SelectionKey> set = serverSelector.selectedKeys();
                        Iterator<SelectionKey> keyIterator = set.iterator();

                        while (keyIterator.hasNext()) {
                            SelectionKey key = keyIterator.next();
                            if (key.isAcceptable()) {
                                SocketChannel clientChannel = ((ServerSocketChannel) key.channel()).accept();
                                clientChannel.configureBlocking(false);
                                clientChannel.register(clientReadSelector, SelectionKey.OP_READ);
                            }
                            keyIterator.remove();
                        }
                    }
