    public OutputData syncProcess() throws InterruptedException {
        int exitCode = this.process.waitFor();
        String stdout = this.outReceiver.getOutput();
        String stderr = this.errReceiver.getOutput();
        OutputData data = new OutputData(stdout, stderr, exitCode);
        for (ExtCmdEndedListener listener : extCmdEndedListenerSet) {
            listener.onEnd(data);
        }
        return data;
    }

    private void checkForConditions() {
        for (Map.Entry<ExtCmdCondition, String> entry : conditionsWithDescriptions.entrySet()) {
            if (!entry.getKey().isSatisfied()) {
                throw new IllegalStateException("Condition not satisfied: " + entry.getValue());
            }
        }
    }

    public static class OutputData {
        public final String stdout;
        public final String stderr;
        public final int exitCode;

        public OutputData(String stdout, String stderr, int exitCode) {
            this.stdout = stdout;
            this.stderr = stderr;
            this.exitCode = exitCode;
        }
    }

    public interface ExtCmdCondition {
        boolean isSatisfied();
    }

    public interface ExtCmdEndedListener {
        void onEnd(OutputData data);
    }
}

