            return result;
        }).when(spyManager).addConnection(any(Connection.class));

        JedisClientConfig spyConfig = DefaultJedisClientConfig.builder().authXManager(spyManager).build();

        ExecutorService executorService = Executors.newFixedThreadPool(2);
        CountDownLatch latch = new CountDownLatch(1);

        try (JedisCluster jc = new JedisCluster(Collections.singleton(hostAndPort), spyConfig)) {
            Runnable task = () -> {
                while (latch.getCount() > 0) {
                    jc.set("foo", "bar");
                }
            };
            Future task1 = executorService.submit(task);
            Future task2 = executorService.submit(task);

            await().pollInterval(ONE_HUNDRED_MILLISECONDS).atMost(ONE_SECOND)
                    .until(connections::size, greaterThanOrEqualTo(2));

            connections.forEach(conn -> {
                verify(conn, atLeast(2)).reAuthenticate();
            });

            latch.countDown();
            task1.get();
            task2.get();
        } finally {
            latch.countDown();
            executorService.shutdown();
        }
    }
}

