import io.lettuce.core.ClientOptions;
import io.lettuce.core.RedisClient;
import io.lettuce.core.SslOptions;
import io.lettuce.core.api.StatefulRedisConnection;
import javax.net.ssl.SNIHostName;
import javax.net.ssl.SNIServerName;
import javax.net.ssl.SSLParameters;
import java.io.File;
import java.util.ArrayList;
import java.util.List;

public class RedisSSLConnection {

    private RedisClient redisClient;
    private StatefulRedisConnection<String, String> connection;

    public RedisSSLConnection(String redisUri, String hostName, String truststorePath, String truststorePassword) {
        this.redisClient = RedisClient.create(redisUri);

        List<SNIServerName> serverNames = new ArrayList<>();
        serverNames.add(new SNIHostName(hostName));

        SslOptions sslOptions = SslOptions.builder()
                .jdkSslProvider()
                .truststore(new File(truststorePath), truststorePassword)
                .sslParameters(() -> {
                    SSLParameters parameters = new SSLParameters();
                    parameters.setServerNames(serverNames);
                    return parameters;
                }).build();

        ClientOptions clientOptions = ClientOptions.builder()
                .sslOptions(sslOptions)
                .build();

        redisClient.setOptions(clientOptions);
        this.connection = redisClient.connect();
    }

    public StatefulRedisConnection<String, String> getConnection() {
        return this.connection;
    }

    public void close() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown();
        }
    }
}

